import xml.etree.ElementTree as ET

try:
    root = ET.fromstring(map_xml_string)
    player_pos = None

    # Find player position
    for row in root.findall("Row"):
        for tile in row.findall("Tile"):
            if tile.find("Player") is not None:
                player_pos = (int(tile.get("id")), int(row.get("id")))
                break
        if player_pos: break

    if not player_pos:
        print("Error: Player not found in Mental Map.")
    else:
        px, py = player_pos
        radius = input_data.get('radius', 2)
        map_id = root.get('id')
        print(f"Scanning radius {radius} around {player_pos} on map {map_id}:")

        for y in range(py - radius, py + radius + 1):
            row = root.find(f"./Row[@id='{y}']")
            if row is not None:
                row_str = ""
                for x in range(px - radius, px + radius + 1):
                    tile = row.find(f"./Tile[@id='{x}']")
                    if tile is not None:
                        t_type = tile.get("type")
                        row_str += f"[{x},{y}: {t_type}] "
                    else:
                        row_str += f"[{x},{y}: N/A] "
                print(row_str)
            else:
                print(f"Row {y} not found.")

except Exception as e:
    print(f"Error executing scan_surroundings: {e}")
