import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(start_x, start_y, end_x, end_y, map_xml, can_surf=False):
    try:
        root = ET.fromstring(map_xml)
    except:
        return None
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            has_obj = tile.attrib.get('has-object', 'false') == 'true'
            is_warp = tile.attrib.get('is-warp', 'false') == 'true'
            seen = tile.attrib.get('seen', 'false') == 'true'

            walkable = ['FLOOR', 'TALL_GRASS', 'DOOR', 'WARP_CARPET_DOWN', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT', 'WARP_CARPET_UP', 'STAIRCASE', 'WARP', 'WARP_PANEL']
            if can_surf:
                walkable.append('WATER')

            if not seen:
                grid[(x, y)] = True
            elif t_type in walkable and not has_obj:
                grid[(x, y)] = True
            elif is_warp:
                grid[(x, y)] = True
            else:
                grid[(x, y)] = False

    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    while queue: 
        curr_x, curr_y, path = queue.popleft()

        if curr_x == end_x and curr_y == end_y:
            return path + [{'x': curr_x, 'y': curr_y}]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if grid.get((nx, ny), False):
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{'x': curr_x, 'y': curr_y}]))
    return None

