import re
import json

def parse_battle_screen(screen_text):
    cleaned_text = screen_text.replace('\n', ' ').replace(':', '')

    pattern = re.compile(
        r"(?P<opponent_name>[A-Z'’]+)\s*"
        r"LV\s*(?P<opponent_level>\d+)\s*"
        r"(?P<player_name>[A-Z'’]+)\s*"
        r"(?:LV\s*\d+\s*)?"
        r"(?P<player_current_hp>\d+)/(?P<player_max_hp>\d+)"
    )

    match = pattern.search(cleaned_text)

    if match:
        data = match.groupdict()

        enemy_json = json.dumps({
            "name": data['opponent_name'].upper(),
            "level": int(data['opponent_level']),
            "hp_percentage": 100
        })

        player_json = json.dumps({
            "name": data['player_name'].upper(),
            "current_hp": int(data['player_current_hp']),
            "max_hp": int(data['player_max_hp'])
        })

        result = {
            "enemy_pokemon_json": enemy_json,
            "player_active_pokemon_json": player_json
        }
        print(json.dumps(result))
    else:
        print(json.dumps({"error": "Failed to parse battle screen text", "input": cleaned_text}))

