import collections
import json
import sys

def solve():
    try:
        input_str = sys.stdin.read()
        if not input_str: return []
        input_data = json.loads(input_str)
        px_start = input_data.get('player_x', 5)
        py_start = input_data.get('player_y', 1)
    except:
        px_start, py_start = 5, 1

    width, height = 10, 18
    walls = set()
    for x in range(10): walls.add((x,0))
    walls.update({(0,2),(1,2),(4,2),(6,2),(4,3),(6,3),(4,4),(6,4),(7,4),(8,4),(9,4),(4,5),(4,6),(6,6),(7,6),(4,7),(2,8),(3,8),(4,8),(8,8),(4,9),(8,9),(2,10),(4,10),(5,10),(7,10),(2,11),(7,11),(2,12),(4,12),(5,12),(9,12),(2,13),(9,13),(2,14),(7,14),(9,14),(2,15),(7,15),(9,15),(2,16),(5,16),(9,16),(2,17),(5,17),(9,17)})
    npcs = {(4,1),(9,2),(4,11),(6,8),(1,15)}
    walls.update(npcs)
    pits = {(2,5), (8,3), (8,7)}
    b_start = tuple(sorted([(3,3), (6,1), (8,14)]))
    q = collections.deque([( (px_start, py_start), b_start, (), [] )])
    v = {( (px_start, py_start), b_start, () )}
    limit = 500000
    while q and limit > 0:
        limit -= 1
        (px, py), b, f, path = q.popleft()
        if len(f) == 3: return path
        for dx, dy, btn in [(0,1,'Down'),(0,-1,'Up'),(1,0,'Right'),(-1,0,'Left')]:
            nx, ny = px+dx, py+dy
            if (nx, ny) in b:
                bx, by = nx+dx, ny+dy
                if 0 <= bx < width and 0 <= by < height and (bx, by) not in walls and (bx, by) not in b:
                    nb = list(b); nb.remove((nx,ny))
                    nf = list(f)
                    if (bx, by) in pits: nf.append((bx, by))
                    else: nb.append((bx, by))
                    st = ((nx, ny), tuple(sorted(nb)), tuple(sorted(nf)))
                    if st not in v: v.add(st); q.append(((nx, ny), tuple(sorted(nb)), tuple(sorted(nf)), path + [btn]))
            elif 0 <= nx < width and 0 <= ny < height and (nx, ny) not in walls and (nx, ny) not in pits:
                st = ((nx, ny), b, f)
                if st not in v: v.add(st); q.append(((nx, ny), b, f, path + [btn]))
    return []

print(json.dumps(solve()))
