    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]

    impassable_tiles = {'WALL', 'HEADBUTT_TREE', 'CUT_TREE', 'WATER', 'COUNTER', 'MART_SHELF', 'BUOY', 'TOWN_MAP', 'WINDOW', 'BOOKSHELF', 'TV', 'RADIO', 'VOID'}

    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            seen = tile_elem.attrib['seen']

            if seen == 'false' or tile_elem.find('Object') is not None or tile_type in impassable_tiles:
                grid[y][x] = 'WALL'
            else:
                grid[y][x] = tile_type

    start = (start_x, start_y)
    end = (end_x, end_y)

