            if (next_x, next_y) in visited:
                continue

            next_tile_type = grid[next_y][next_x]
            if next_tile_type == 'WALL':
                continue

            current_tile_type = grid[current_y][current_x]
            if dy == 1 and 'LEDGE' in current_tile_type: 
