import heapq
import xml.etree.ElementTree as ET
import json

def run_path_master(start_x_str, start_y_str, end_x_str, end_y_str):
    start_x, start_y = int(start_x_str), int(start_y_str)
    end_x, end_y = int(end_x_str), int(end_y_str)

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        print(f"Error parsing map_xml_string: {e}")
        return

    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = [['VOID' for _ in range(width + 1)] for _ in range(height + 1)]
    objects = set()

    impassable_types = {'WALL', 'WATER', 'CUT_TREE', 'BUOY', 'TOWN_MAP', 'WINDOW', 'BOOKSHELF', 'TV', 'RADIO', 'VOID', 'MART_SHELF', 'COUNTER', 'PC', 'HEADBUTT_TREE', 'PILLAR'}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')

            grid[y][x] = tile_type

            if tile_elem.get('seen') == 'false':
                grid[y][x] = 'unseen'

            if tile_elem.find('Object') is not None:
                objects.add((y, x))

    def get_possible_moves(y, x):
        tile_type = grid[y][x]
        if 'FLOOR_HOP_DOWN_LEDGE' in tile_type:
            return [(1, 0)]
        if 'FLOOR_HOP_RIGHT_LEDGE' in tile_type:
            return [(0, 1)]
        if 'FLOOR_HOP_LEFT_LEDGE' in tile_type:
            return [(0, -1)]
        if 'FLOOR_HOP_DOWN_OR_RIGHT_LEDGE' in tile_type:
            return [(1, 0), (0, 1)]
        if 'FLOOR_HOP_DOWN_OR_LEFT_LEDGE' in tile_type:
            return [(1, 0), (0, -1)]
        return [(-1, 0), (1, 0), (0, -1), (0, 1)]

    def is_traversable(y, x, from_y, from_x):
        if not (0 <= y <= height and 0 <= x <= width):
            return False
        tile_type = grid[y][x]
        if tile_type in impassable_types or tile_type == 'unseen' or (y, x) in objects:
            return False

        if y < from_y and 'LEDGE' in tile_type:
            return False

        return True

    start = (start_y, start_x)
    end = (end_y, end_x)

    if not (0 <= start[0] <= height and 0 <= start[1] <= width):
        print(f"Start {start_x, start_y} is out of bounds.")
        return

    pq = [(0, start, [])]
    visited = set()
    cost_so_far = {start: 0}

    while pq:
        _, current, path = heapq.heappop(pq)
        y, x = current

        if current in visited:
            continue

        visited.add(current)
        new_path = path + [current]

        if current == end:
            path_coords = [{"x": px, "y": py} for py, px in new_path]
            print(json.dumps(path_coords))
            return

        for dy, dx in get_possible_moves(y, x):
            ny, nx = y + dy, x + dx
            neighbor = (ny, nx)

            if is_traversable(ny, nx, y, x):
                new_cost = cost_so_far[current] + 1
                if neighbor not in cost_so_far or new_cost < cost_so_far[neighbor]:
                    cost_so_far[neighbor] = new_cost
                    heuristic = abs(ny - end[0]) + abs(nx - end[1])
                    priority = new_cost + heuristic
                    heapq.heappush(pq, (priority, neighbor, new_path))

    print("No path found")

