import xml.etree.ElementTree as ET
from collections import deque
import json

def find_unseen_logic(map_xml_string, start_x, start_y):
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]
    unseen_coords = set()
    impassable_tiles = {'WALL', 'HEADBUTT_TREE', 'CUT_TREE', 'WATER', 'COUNTER', 'MART_SHELF', 'BUOY', 'TOWN_MAP', 'WINDOW', 'BOOKSHELF', 'TV', 'RADIO', 'VOID'}

    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            seen = tile_elem.attrib['seen']

            if seen == 'false':
                unseen_coords.add((x, y))
                grid[y][x] = 'UNSEEN'
            elif tile_elem.find('Object') is not None or tile_type in impassable_tiles:
                grid[y][x] = 'WALL'
            else:
                grid[y][x] = tile_type

    start = (start_x, start_y)
    if not (1 <= start[0] <= width and 1 <= start[1] <= height):
        return json.dumps({"error": "Start coordinates are out of bounds."})

    if not unseen_coords:
        return json.dumps({"error": "No unseen tiles on the map."})

    queue = deque([start])
    visited = {start}

    while queue:
        (current_x, current_y) = queue.popleft()

        moves = [(0, -1), (0, 1), (-1, 0), (1, 0)]

        for dx, dy in moves:
            next_x, next_y = current_x + dx, current_y + dy

            if not (1 <= next_x <= width and 1 <= next_y <= height):
                continue

            if (next_x, next_y) in unseen_coords:
                return json.dumps({"goal_x": current_x, "goal_y": current_y})

            next_tile_type = grid[next_y][next_x]
            if (next_x, next_y) not in visited and next_tile_type != 'WALL' and next_tile_type != 'UNSEEN':
                # Add ledge check
                current_tile_type = grid[current_y][current_x]
                if dy == -1 and 'LEDGE' in next_tile_type: # Trying to move up a ledge
                    continue

                visited.add((next_x, next_y))
                queue.append((next_x, next_y))

    return json.dumps({"error": "No path to any unseen tile found."})

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])

result = find_unseen_logic(map_xml_string, start_x, start_y)
print(result)
