import heapq
import xml.etree.ElementTree as ET
import json

class Node:
    def __init__(self, position, parent=None):
        self.parent = parent
        self.position = position
        self.g = 0
        self.h = 0
        self.f = 0

    def __eq__(self, other):
        return self.position == other.position

    def __lt__(self, other):
        if self.f == other.f:
            return self.h < other.h
        return self.f < other.f

    def __hash__(self):
        return hash(self.position)

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def find_path(grid, start, end, on_screen_objects, debug_info):
    impassable_base = {
        'WALL', 'VOID', 'WATER', 'CUT_TREE', 'COUNTER', 'BUOY', 'HEADBUTT_TREE', 'unseen',
        'MART_SHELF', 'BOOKSHELF', 'TV', 'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'STATUE', 'PILLAR',
        'BED', 'TABLE', 'CHAIR'
    }
    warp_tiles = {'DOOR', 'CAVE', 'LADDER', 'WARP_CARPET_DOWN', 'WARP_CARPET_RIGHT', 'WARP_CARPET_LEFT'}
    hop_down_tiles = {'LEDGE', 'FLOOR_ALLOW_HOP_DOWN'}

    start_node = Node(start)
    end_node = Node(end)

    if start not in grid or grid[start]['type'] in impassable_base or start in on_screen_objects:
        debug_info['error'] = f"Start position {start} is impassable."
        return None
    if end not in grid or (grid[end]['type'] in impassable_base and grid[end]['type'] not in warp_tiles) or (end in on_screen_objects and grid[end]['type'] not in warp_tiles):
        debug_info['error'] = f"End position {end} is impassable."
        return None

    open_list = []
    closed_set = set()
    heapq.heappush(open_list, start_node)

    while open_list:
        current_node = heapq.heappop(open_list)

        if current_node.position in closed_set:
            continue
        closed_set.add(current_node.position)

        if current_node == end_node:
            path = []
            current = current_node
            while current is not None:
                path.append(list(current.position))
                current = current.parent
            return path[::-1]

        for move_x, move_y, move_name in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            current_pos = current_node.position
            next_pos = (current_pos[0] + move_x, current_pos[1] + move_y)

            if next_pos not in grid: continue

            current_type = grid[current_pos]['type']
            next_type = grid[next_pos]['type']

            if next_type in impassable_base: continue
            if next_pos in on_screen_objects: continue
            if next_type in warp_tiles and next_pos != end: continue

            if move_name == 'Up' and current_type in hop_down_tiles:
                continue

            child = Node(next_pos, current_node)
            child.g = current_node.g + 1
            child.h = heuristic(child.position, end_node.position)
            child.f = child.g + child.h

            if any(open_node for open_node in open_list if open_node == child and child.g >= open_node.g):
                continue

            heapq.heappush(open_list, child)

    debug_info['nodes_checked'] = len(closed_set)
    debug_info['error'] = 'No path found to destination.'
    return None

