import heapq
import json
import xml.etree.ElementTree as ET

def parse_map(map_xml_string):
    root = ET.fromstring(map_xml_string)
    player_grid = {}
    farfetchd_grid = {}
    player_impassable = {'WALL', 'VOID', 'WATER', 'CUT_TREE', 'HEADBUTT_TREE', 'unseen'}
    farfetchd_impassable = {'WALL', 'VOID', 'WATER', 'unseen'}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_type = tile.get('type')
            player_grid[(x, y)] = tile_type not in player_impassable
            farfetchd_grid[(x, y)] = tile_type not in farfetchd_impassable
    return player_grid, farfetchd_grid

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_path(grid, start, end):
    if not grid.get(start) or not grid.get(end):
        return None
    open_list = []
    heapq.heappush(open_list, (0, start))
    came_from = {start: None}
    cost_so_far = {start: 0}
    while open_list:
        _, current = heapq.heappop(open_list)
        if current == end:
            path = []
            while current is not None:
                path.append(current)
                current = came_from[current]
            return path[::-1]
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if grid.get(neighbor):
                new_cost = cost_so_far[current] + 1
                if neighbor not in cost_so_far or new_cost < cost_so_far[neighbor]:
                    cost_so_far[neighbor] = new_cost
                    priority = new_cost + heuristic(end, neighbor)
                    heapq.heappush(open_list, (priority, neighbor))
                    came_from[neighbor] = current
    return None

try:
    player_start = (22, 29)
    farfetchd_start = (22, 27)
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    farfetchd_target = (target_x, target_y)
    twigs = {(22, 30), (23, 30), (20, 28), (20, 31), (17, 30), (15, 28), (12, 28), (12, 31), (10, 30), (8, 28)}
    player_grid, farfetchd_grid = parse_map(map_xml_string)

    initial_state = (player_start, farfetchd_start)
    queue = [(heuristic(farfetchd_start, farfetchd_target), [], initial_state)]
    visited_states = {initial_state}
    solution_path = None

    while queue:
        p, path, current_state = heapq.heappop(queue)
        player_pos, farfetchd_pos = current_state
        if farfetchd_pos == farfetchd_target:
            solution_path = path
            break

        for twig_pos in twigs:
            player_path_to_twig = a_star_path(player_grid, player_pos, twig_pos)
            if not player_path_to_twig: continue

            if len(player_path_to_twig) > 1:
                face_x, face_y = twig_pos[0] - player_path_to_twig[-2][0], twig_pos[1] - player_path_to_twig[-2][1]
            else: # Already adjacent
                face_x, face_y = twig_pos[0] - player_pos[0], twig_pos[1] - player_pos[1]

            new_farfetchd_pos = (farfetchd_pos[0] - face_x, farfetchd_pos[1] - face_y)

            if farfetchd_grid.get(new_farfetchd_pos):
                new_state = (twig_pos, new_farfetchd_pos)
                if new_state not in visited_states:
                    visited_states.add(new_state)
                    new_player_path = path + player_path_to_twig[1:]
                    priority = len(new_player_path) + heuristic(new_farfetchd_pos, farfetchd_target) * 2
                    heapq.heappush(queue, (priority, new_player_path, new_state))

    buttons = []
    if solution_path:
        current_pos = player_start
        for next_pos in solution_path:
            dx, dy = next_pos[0] - current_pos[0], next_pos[1] - current_pos[1]
            if dy == 1: buttons.append('Down')
            elif dy == -1: buttons.append('Up')
            elif dx == 1: buttons.append('Right')
            elif dx == -1: buttons.append('Left')
            current_pos = next_pos
        buttons.append('A')

    print(json.dumps({'buttons': buttons, 'path': solution_path}))
except Exception as e:
    import traceback
    print(json.dumps({'error': str(e), 'traceback': traceback.format_exc()}))
