import json

def find_node_by_coords(graph, map_id, x, y):
    for node in graph.get('nodes', []):
        if str(node.get('map_id')) == str(map_id) and node['coordinates']['x'] == x and node['coordinates']['y'] == y:
            return node
    return None

def find_edge(graph, source_id, dest_id):
    for edge in graph.get('edges', []):
        if (edge['source_node_id'] == source_id and edge['destination_node_id'] == dest_id) or \
           (edge['source_node_id'] == dest_id and edge['destination_node_id'] == source_id and not edge.get('is_one_way')):
            return edge
    return None

operation = input_data['operation']
graph = json.loads(world_knowledge_graph_json_string)

if operation == 'add_node':
    map_id = input_data['map_id']
    x = int(input_data['x'])
    y = int(input_data['y'])
    name = input_data['name']
    node_type = input_data.get('type', '')
    tags = json.loads(input_data.get('tags', '[]'))

    existing_node = find_node_by_coords(graph, map_id, x, y)
    if existing_node:
        print(json.dumps({'status': 'skipped', 'reason': 'Node already exists.', 'node_id': existing_node['id']}))
    else:
        payload = {'map_id': map_id, 'name': name, 'coordinates': {'x': x, 'y': y}, 'type': node_type, 'tags': tags}
        tool_call = {'tool_name': 'manage_world_knowledge', 'tool_args': [{'name': 'action', 'value': 'add_node'}, {'name': 'payload', 'value': json.dumps(payload)}]}
        print(json.dumps({'action': 'call_tool', 'tool_calls': [tool_call]}))

elif operation == 'add_edge':
    source_map_id = input_data['source_map_id']
    source_x = int(input_data['source_x'])
    source_y = int(input_data['source_y'])
    dest_map_id = input_data['dest_map_id']
    dest_x = int(input_data['dest_x'])
    dest_y = int(input_data['dest_y'])
    connection_type = input_data['connection_type']
    is_one_way = input_data.get('is_one_way', 'false').lower() == 'true'
    dest_entry_point = input_data.get('destination_entry_point')

    source_node = find_node_by_coords(graph, source_map_id, source_x, source_y)
    dest_node = find_node_by_coords(graph, dest_map_id, dest_x, dest_y)

    if not source_node:
        print(json.dumps({'status': 'error', 'reason': f'Source node at ({source_x},{source_y}) on map {source_map_id} not found.'}))
    elif not dest_node:
        print(json.dumps({'status': 'error', 'reason': f'Destination node at ({dest_x},{dest_y}) on map {dest_map_id} not found.'}))
    else:
        existing_edge = find_edge(graph, source_node['id'], dest_node['id'])
        if existing_edge:
            print(json.dumps({'status': 'skipped', 'reason': 'Edge already exists.', 'edge_id': existing_edge['id']}))
        else:
            payload = {
                'source_node_id': source_node['id'],
                'destination_node_id': dest_node['id'],
                'connection_type': connection_type,
                'source_coordinates': {'x': source_x, 'y': source_y, 'map_id': source_map_id},
                'destination_coordinates': {'x': dest_x, 'y': dest_y, 'map_id': dest_map_id},
                'is_one_way': is_one_way
            }
            if dest_entry_point is not None and connection_type == 'warp':
                payload['destination_entry_point'] = int(dest_entry_point)

            tool_call = {'tool_name': 'manage_world_knowledge', 'tool_args': [{'name': 'action', 'value': 'add_edge'}, {'name': 'payload', 'value': json.dumps(payload)}]}
            print(json.dumps({'action': 'call_tool', 'tool_calls': [tool_call]}))
else:
    print(json.dumps({'status': 'error', 'reason': 'Invalid operation.'}))
