import xml.etree.ElementTree as ET
from collections import deque
import json

def get_map_data(map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    grid = {}
    objects = set()
    start_x, start_y = None, None

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem.get('type')
            if tile_elem.find('Player') is not None:
                start_x, start_y = x, y
            if tile_elem.find('Object') is not None:
                obj = tile_elem.find('Object')
                if obj.get('id-name') != 'PIKACHU':
                    objects.add((x, y))
    return map_width, map_height, grid, objects, start_x, start_y

def has_conscious_hm_user(party, hm_name):
    for pokemon in party:
        if not pokemon.get('is_fainted', False):
            for move in pokemon.get('moves', []):
                if move.get('name') == hm_name:
                    return True
    return False

def is_valid_move(grid, current_pos, next_pos, objects, party):
    current_type = grid.get(current_pos)
    next_type = grid.get(next_pos)
    walkable_land = ['ground', 'grass', 'steps']

    if next_pos in objects or next_type is None:
        return False

    if next_type == 'water' and current_type in walkable_land:
        if not has_conscious_hm_user(party, 'SURF'):
            return False

    if current_type == 'water':
        if next_type == 'water' or next_type in walkable_land:
            return True
    elif current_type in walkable_land:
        if next_type in walkable_land or next_type == 'water':
            return True
    elif current_type == 'steps' and next_type in ['ground', 'elevated_ground']:
        return True
    elif next_type == 'steps' and current_type in ['ground', 'elevated_ground']:
        return True

    if next_type == 'ledge':
        if next_pos[1] > current_pos[1] and next_pos[0] == current_pos[0]:
            return True

    return False

def find_path(map_xml_string, target_x, target_y, party_data_str):
    try:
        party = json.loads(party_data_str)
    except json.JSONDecodeError:
        return None, 'Invalid party_data JSON.'

    map_width, map_height, grid, objects, start_x, start_y = get_map_data(map_xml_string)

    if start_x is None:
        return None, 'Player start position not found.'

    q = deque([((start_x, start_y), [])])
    visited = {(start_x, start_y)}

    traversable_types = ['ground', 'grass', 'water', 'steps', 'ledge']
    target_tile_type = grid.get((target_x, target_y), 'impassable')
    target_is_impassable = target_tile_type not in traversable_types or (target_x, target_y) in objects

    while q:
        (current_x, current_y), path = q.popleft()

        if target_is_impassable:
            if abs(current_x - target_x) + abs(current_y - target_y) == 1:
                if grid.get((current_x, current_y)) in traversable_types and (current_x, current_y) not in objects:
                    return path, None
        elif (current_x, current_y) == (target_x, target_y):
            return path, None

        moves = [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]

        for dx, dy, move in moves:
            next_x, next_y = current_x + dx, current_y + dy
            next_pos = (next_x, next_y)

            if not (1 <= next_x <= map_width and 1 <= next_y <= map_height) or next_pos in visited:
                continue

            if is_valid_move(grid, (current_x, current_y), next_pos, objects, party):
                next_path = path + [move]
                if grid.get(next_pos) == 'ledge':
                    land_pos = (next_x, next_y + 1)
                    if 1 <= land_pos[1] <= map_height and land_pos not in visited:
                        visited.add(next_pos)
                        visited.add(land_pos)
                        q.append((land_pos, next_path))
                else:
                    visited.add(next_pos)
                    q.append((next_pos, next_path))
    return None, 'No path found.'

try:
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    party_data_str = input_data['party_data']
    path_result, error_msg = find_path(map_xml_string, target_x, target_y, party_data_str)
    if path_result is not None:
        print(json.dumps({"path_found": True, "steps": path_result}))
    else:
        print(json.dumps({"path_found": False, "error": error_msg}))
except Exception as e:
    print(json.dumps({"path_found": False, "error": f"An unexpected error occurred: {str(e)}"}))

