import json
import xml.etree.ElementTree as ET
from collections import deque

def navigate_to_unseen():
    tree = ET.fromstring(map_xml_string)
    width = int(tree.attrib['width'])
    height = int(tree.attrib['height'])

    player_pos = None
    tiles = {}
    for row in tree.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            seen = tile.attrib['seen'] == 'true'
            t_type = tile.attrib['type']
            has_player = tile.find('Player') is not None

            # Extract objects
            obj = tile.find('Object')
            is_wall = has_player is False and obj is not None

            tiles[(x, y)] = {'seen': seen, 'type': t_type, 'is_wall': is_wall}
            if has_player:
                player_pos = (x, y)

    if not player_pos:
        return []

    # BFS to find nearest unseen reachable tile
    queue = deque([(player_pos, [])])
    visited = {player_pos}

    while queue:
        (cx, cy), path = queue.popleft()

        # Check neighbors
        for dx, dy, btn in [(0, 1, "Down"), (0, -1, "Up"), (1, 0, "Right"), (-1, 0, "Left")]:
            nx, ny = cx + dx, cy + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    tile = tiles.get((nx, ny))
                    if tile:
                        if not tile['seen']:
                            return path + [btn]

                        # Traversal rules
                        can_pass = False
                        if tile['type'] == 'FLOOR' and not tile['is_wall']:
                            can_pass = True
                        # Add more tile types if needed

                        if can_pass:
                            visited.add((nx, ny))
                            queue.append(((nx, ny), path + [btn]))
    return []

print(json.dumps(navigate_to_unseen()))

