import json
def get_btns(item_idx, pkmn_idx, cur_idx):
    btns = ["Start", "sleep 500"]
    # Target PACK is Index 2
    diff = 2 - cur_idx
    if diff > 0:
        for _ in range(diff): btns.append("Down")
    elif diff < 0:
        for _ in range(abs(diff)): btns.append("Up")
    btns.append("A")
    btns.append("sleep 500")
    # In Bag. Assumes Items pocket.
    for _ in range(item_idx): btns.append("Down")
    btns.append("A")
    btns.extend(["sleep 300", "A", "sleep 500"])
    # In Party menu. Cursor on Slot 1.
    for _ in range(pkmn_idx - 1): btns.append("Down")
    btns.append("A")
    # Success text and close
    btns.extend(["sleep 1000", "B", "B", "B"])
    return btns
