import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    noisy = {(t['x'], t['y']) for t in input_data.get('noisy_tiles', [])}

    # Find player
    player_tile = root.find(".//Tile[Player]")
    if player_tile is None:
        print(json.dumps([]))
        return
    start_x = int(player_tile.get('id'))
    start_y = int(player_tile.get('coordinate').split(',')[1].strip(' )'))

    # Map collision and objects
    grid = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            has_obj = tile.get('has-object') == 'true'
            # Treat all objects as walls (including the bird)
            grid[(x, y)] = {'type': t_type, 'blocked': has_obj or t_type in ['WALL', 'DENSE_TREES', 'HEADBUTT_TREE', 'CUT_TREE', 'WATER', 'VOID']}

    # BFS
    queue = deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        curr_x, curr_y, path = queue.popleft()

        if curr_x == target_x and curr_y == target_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if (nx, ny) in noisy:
                    continue # Avoid noisy tiles

                tile = grid.get((nx, ny))
                if tile and not tile['blocked']:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
                elif tile and tile['type'].startswith('LEDGE_HOP'):
                    # Simple ledge handling for now
                    pass

    print(json.dumps([]))

solve()
