import xml.etree.ElementTree as ET

root = ET.fromstring(map_xml_string)
gates = []
for row_elem in root.findall('Row'):
    y = row_elem.get('id')
    for tile_elem in row_elem.findall('Tile'):
        tile_type = tile_elem.get('type')
        if 'gate' in tile_type:
            x = tile_elem.get('id')
            gates.append(f'Gate at ({x},{y}) is {tile_type}')

if not gates:
    print('No gates found on the current map.')
else:
    print('\n'.join(gates))
