import json
import sys
import re

def main():
    try:
        input_raw = sys.stdin.read()
        if not input_raw:
            print(json.dumps(["A"]))
            return
        input_data = json.loads(input_raw)
        target_index = int(input_data.get('move_index', 1))
        screen_text = input_data.get('screen_text', "")

        # Split into lines and filter for move slots
        lines = screen_text.split('\n')
        move_lines = []
        for line in lines:
            # Move lines contain a vertical bar and are not borders
            if '\u2502' in line and not any(c in line for c in ['\u250c', '\u2510', '\u2514', '\u2518', '\u2500']):
                # Exclude lines that are clearly not moves (e.g. type display, pocket names)
                if not any(x in line for x in ['TYPE/', 'POCKET', 'ITEMS']):
                    # Check for move content: alpha chars or hyphen (for empty slot '-')
                    if re.search(r'[A-Za-z-]', line):
                        move_lines.append(line)

        # Determine current index by searching for cursor ▶ (\u25b6) or >
        current_index = 1
        found_cursor = False
        for i, line in enumerate(move_lines):
            if '\u25b6' in line or '▶' in line or '\u25ba' in line or '▶' in line:
                current_index = i + 1
                found_cursor = True
                break

        buttons = []
        if current_index != target_index:
            diff = target_index - current_index
            # Shortest path wrapping logic for 4 items
            if diff == 1 or diff == -3:
                buttons.append("Down")
            elif diff == 2 or diff == -2:
                buttons.extend(["Down", "Down"])
            elif diff == 3 or diff == -1:
                buttons.append("Up")

        buttons.append("A")
        print(json.dumps(buttons))
    except Exception:
        # Fallback to just pressing A if something fails
        print(json.dumps(["A"]))

if __name__ == "__main__":
    main()

