import heapq
import json
import xml.etree.ElementTree as ET

def find_path(start_x, start_y, end_x, end_y, temp_impassable_coords, map_xml_string):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    tile_grid = [[None for _ in range(width)] for _ in range(height)]
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id')) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id')) - 1
            tile_grid[y][x] = tile_elem

    start = (start_x - 1, start_y - 1)
    end = (end_x - 1, end_y - 1)

    traversable_tiles = {'ground', 'grass', 'cuttable', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'teleport', 'gate_offscreen', 'open_gate', 'water', 'ladder_up', 'ladder_down', 'hole'}

    temp_impassable = set()
    if temp_impassable_coords:
        try:
            coords_list = json.loads(temp_impassable_coords)
            for coord in coords_list:
                temp_impassable.add((int(coord['x']) - 1, int(coord['y']) - 1))
        except (json.JSONDecodeError, KeyError, TypeError):
            pass

    def get_tile_info(x, y):
        if not (0 <= x < width and 0 <= y < height):
            return None, "out_of_bounds"

        tile_elem = tile_grid[y][x]
        if tile_elem is None:
            return None, "unknown"

        tile_type = tile_elem.get('type')

        if tile_elem.find('Boulder') is not None:
            return tile_elem, "Boulder"

        obj_elem = tile_elem.find('Object')
        if obj_elem is not None:
            obj_id_name = obj_elem.get('id-name', 'Unknown Object')
            if 'Pikachu' in obj_id_name:
                return tile_elem, "Pikachu"
            return tile_elem, obj_elem.get('name', obj_id_name)

        return tile_elem, tile_type

    def is_traversable(x, y):
        if (x, y) in temp_impassable:
            return False
        tile_elem, info = get_tile_info(x, y)
        if info == "Pikachu":
            return True
        return info in traversable_tiles

    goal_positions = []
    if is_traversable(end[0], end[1]):
        goal_positions.append(end)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = end[0] + dx, end[1] + dy
            if is_traversable(nx, ny):
                goal_positions.append((nx, ny))

    if not goal_positions:
        _, end_info = get_tile_info(end[0], end[1])
        error_result = {"path": [], "error": "Destination ({}, {}) and all adjacent tiles are impassable. Destination contains: {}."}.format(end_x, end_y, end_info)
        return json.dumps(error_result)

    def is_valid_transition(current_type, neighbor_type):
        if current_type == 'water' and neighbor_type == 'water':
            return True
        ground_level = {'ground', 'grass', 'water', 'cuttable', 'cleared_boulder_barrier', 'teleport', 'gate_offscreen', 'open_gate', 'hole'}
        elevated_level = {'elevated_ground'}
        ladder_types = {'ladder_up', 'ladder_down'}

        if current_type in ladder_types and (neighbor_type in ground_level or neighbor_type in elevated_level):
            return True
        if neighbor_type in ladder_types and (current_type in ground_level or current_type in elevated_level):
            return True

        if current_type == 'steps':
            return neighbor_type in ground_level or neighbor_type in elevated_level or neighbor_type == 'steps'
        if neighbor_type == 'steps':
            return current_type in ground_level or current_type in elevated_level or current_type == 'steps'

        if (current_type in ground_level and neighbor_type in ground_level):
            return True
        if (current_type in elevated_level and neighbor_type in elevated_level):
            return True

        return False

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (min(heuristic(start, goal) for goal in goal_positions), start))

    came_from = {}
    g_score = {start: 0}

    frontier_impassable = set()

    while open_set:
        _, current = heapq.heappop(open_set)

        if current in goal_positions:
            path = []
            while current in came_from:
                path.append({'x': current[0] + 1, 'y': current[1] + 1})
                current = came_from[current]
            path.append({'x': start[0] + 1, 'y': start[1] + 1})
            return json.dumps({"path": path[::-1]})

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            current_x, current_y = current
            neighbor = (current_x + dx, current_y + dy)

            current_tile_elem, current_info = get_tile_info(current_x, current_y)
            if current_tile_elem is None: continue

            is_ledge_jump = False
            if dy == 1 and current_info != 'ledge' and get_tile_info(neighbor[0], neighbor[1])[1] == 'ledge':
                neighbor = (neighbor[0], neighbor[1] + 1)
                is_ledge_jump = True

            neighbor_tile_elem, neighbor_info = get_tile_info(neighbor[0], neighbor[1])
            if neighbor_tile_elem is None: continue

            if dy == -1 and neighbor_info == 'ledge':
                continue

            if not is_traversable(neighbor[0], neighbor[1]):
                if neighbor not in g_score:
                    frontier_impassable.add(neighbor)
                continue

            current_type = current_tile_elem.get('type')
            neighbor_type = neighbor_tile_elem.get('type')

            if not is_ledge_jump and not is_valid_transition(current_type, neighbor_type):
                continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + min(heuristic(neighbor, goal) for goal in goal_positions)
                if not any(item[1] == neighbor for item in open_set):
                    heapq.heappush(open_set, (f_score, neighbor))

    blocking_objects = []
    for pos in frontier_impassable:
        _, info = get_tile_info(pos[0], pos[1])
        blocking_objects.append({"x": pos[0] + 1, "y": pos[1] + 1, "type": info})

    error_message = "No path found."
    if blocking_objects:
        error_message = "Path blocked by one or more obstacles."

    return json.dumps({"path": [], "error": error_message, "blocking_obstacles": blocking_objects})

