import json
import xml.etree.ElementTree as ET

def parse_map_data(map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_data = {
        'name': root.get('name'),
        'id': int(root.get('id')),
        'width': int(root.get('width')),
        'height': int(root.get('height')),
        'tiles': []
    }
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_info = {
                'x': x,
                'y': y,
                'type': tile_elem.get('type'),
                'object': None
            }
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None:
                tile_info['object'] = {
                    'id': obj_elem.get('id'),
                    'id-name': obj_elem.get('id-name'),
                    'name': obj_elem.get('name'),
                    'facing': obj_elem.get('facing')
                }
            map_data['tiles'].append(tile_info)
    return map_data

result = parse_map_data(map_xml_string)
print(json.dumps(result))
