dungeon_navigator.python_script:
import json
import xml.etree.ElementTree as ET

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
            grid[(x, y)] = {
                'type': t.get('type'),
                'has_object': has_impassable_object
            }

    impassable_types = ['impassable', 'unknown', 'cuttable', 'water', 'closed_gate']

    stack = [(start_x, start_y)]
    visited = set()
    path = []

    start_tile = grid.get((start_x, start_y))
    if not start_tile or start_tile['type'] in impassable_types or start_tile['has_object']:
        print(json.dumps({'path_found': False, 'error': 'Starting tile is impassable.'}))
        exit()

    while stack:
        cx, cy = stack.pop()

        if (cx, cy) in visited:
            continue

        visited.add((cx, cy))
        path.append({'x': cx, 'y': cy})

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy

            if (nx, ny) in visited:
                continue

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            neighbor_tile = grid.get((nx, ny))
            if not neighbor_tile or neighbor_tile['type'] in impassable_types or neighbor_tile['has_object']:
                continue

            if neighbor_tile['type'] == 'ledge':
                if dy == -1 or dx != 0:
                    continue

            stack.append((nx, ny))

    print(json.dumps({'path_found': True, 'path': path}))

except Exception as e:
    print(json.dumps({'path_found': False, 'error': str(e)}))

pathfinder.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    ignorable_types_str = input_data.get('ignorable_impassable_types', '')
    ignorable_types = [t.strip() for t in ignorable_types_str.split(',') if t.strip()] if ignorable_types_str else []

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
            grid[(x, y)] = {
                'type': t.get('type'),
                'has_object': has_impassable_object
            }

    base_impassable = ['impassable', 'unknown', 'cuttable', 'water', 'closed_gate']
    impassable_types = [t for t in base_impassable if t not in ignorable_types]

    queue = deque([([ (start_x, start_y) ])])
    visited = {(start_x, start_y)}

    while queue:
        path = queue.popleft()
        cx, cy = path[-1]

        if (cx, cy) == (end_x, end_y):
            print(json.dumps({'path_found': True, 'path': [{'x': p[0], 'y': p[1]} for p in path]}))
            exit()

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy

            if not (1 <= nx <= width and 1 <= ny <= height) or (nx, ny) in visited:
                continue

            neighbor_tile = grid.get((nx, ny))
            if not neighbor_tile:
                continue

            if neighbor_tile['type'] in impassable_types or neighbor_tile['has_object']:
                continue

            if neighbor_tile['type'] == 'ledge' and dy != 1:
                continue

            visited.add((nx, ny))
            new_path = path + [(nx, ny)]
            queue.append(new_path)

    print(json.dumps({'path_found': False, 'path': []}))

except Exception as e:
    print(json.dumps({'path_found': False, 'error': str(e)}))

wkg_checker.python_script:
import json

def find_node_id(nodes, map_id, x, y):
    for node in nodes:
        if node.get('map_id') == map_id and node.get('coordinates', {}).get('x') == x and node.get('coordinates', {}).get('y') == y:
            return node.get('id')
    return None

try:
    wkg = json.loads(world_knowledge_graph_json_string)
    nodes = wkg.get('nodes', [])
    edges = wkg.get('edges', [])

    element_type = input_data['element_type']
    map_id = input_data['map_id']
    x = int(input_data['x'])
    y = int(input_data['y'])

    found = False

    if element_type == 'node':
        if find_node_id(nodes, map_id, x, y) is not None:
            found = True

    elif element_type == 'edge':
        to_map_id = input_data.get('to_map_id')
        to_x = int(input_data.get('to_x'))
        to_y = int(input_data.get('to_y'))

        source_node_id = find_node_id(nodes, map_id, x, y)
        dest_node_id = find_node_id(nodes, to_map_id, to_x, to_y)

        if source_node_id and dest_node_id:
            for edge in edges:
                s_id = edge.get('source_node_id')
                d_id = edge.get('destination_node_id')

                is_direct_match = (s_id == source_node_id and d_id == dest_node_id)
                is_reverse_match = (not edge.get('is_one_way', False) and s_id == dest_node_id and d_id == source_node_id)

                if is_direct_match or is_reverse_match:
                    found = True
                    break

    print(json.dumps({'exists': found}))

except Exception as e:
    print(json.dumps({'exists': False, 'error': str(e)}))

object_finder.python_script:
import json
import xml.etree.ElementTree as ET

try:
    object_name_to_find = input_data['object_name']
    root = ET.fromstring(map_xml_string)

    found_objects = []

    for r in root.findall('Row'):
        for t in r.findall('Tile'):
            for obj in t.findall('Object'):
                id_name = obj.get('id-name', '')
                event_name = obj.get('name', '')

                if object_name_to_find.lower() in id_name.lower() or object_name_to_find.lower() in event_name.lower():
                    found_objects.append({
                        'id-name': id_name,
                        'name': event_name,
                        'x': int(t.get('id')),
                        'y': int(r.get('id'))
                    })

    if found_objects:
        print(json.dumps({'found': True, 'objects': found_objects}))
    else:
        print(json.dumps({'found': False, 'message': f"Object '{object_name_to_find}' not found on the current map."}))

except Exception as e:
    print(json.dumps({'found': False, 'error': str(e)}))

encounter_grinder_tool.python_script:
import json
try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    repetitions = int(input_data['repetitions'])

    path_forward = []
    if end_x > start_x: path_forward.extend(['Right'] * (end_x - start_x))
    if end_x < start_x: path_forward.extend(['Left'] * (start_x - end_x))
    if end_y > start_y: path_forward.extend(['Down'] * (end_y - start_y))
    if end_y < start_y: path_forward.extend(['Up'] * (start_y - end_y))

    path_backward = []
    if start_x > end_x: path_backward.extend(['Right'] * (start_x - end_x))
    if start_x < end_x: path_backward.extend(['Left'] * (end_x - start_x))
    if start_y > end_y: path_backward.extend(['Down'] * (start_y - end_y))
    if start_y < end_y: path_backward.extend(['Up'] * (end_y - start_y))

    full_sequence = []
    for _ in range(repetitions):
        full_sequence.extend(path_forward)
        full_sequence.extend(path_backward)

    print(json.dumps({'button_sequence': full_sequence}))
except Exception as e:
    print(json.dumps({'error': str(e)}))
