import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path_to_target(target_x, target_y):
    root = ET.fromstring(map_xml_string)
    target_x, target_y = int(target_x), int(target_y)

    IMPASSABLE_TILE_TYPES = ['WALL', 'WATER', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'PC', 'MART_SHELF', 'VOID', 'BUOY', 'CAVE']
    COLLECTED_ITEM_EMOJI = '‚úÖ'
    OBSTACLE_MARKER_EMOJIS = ['üìç', '‚ò†Ô∏è', 'üíù', 'üéÅ']
    ONE_WAY_RULES = {
        'LEDGE_HOP_DOWN': (0, 1),
        'LEDGE_HOP_RIGHT': (1, 0),
        'LEDGE_HOP_LEFT': (-1, 0),
        'FLOOR_UP_WALL': (0, -1)
    }

    grid = {}
    tile_elements = {}
    start_x, start_y = -1, -1

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_elements[(x, y)] = tile
            grid[(x, y)] = {'type': tile.get('type')}
            if tile.find('Player') is not None:
                start_x, start_y = x, y

    if start_x == -1:
        print(json.dumps([]))
        return

    def is_passable(x, y):
        tile_info = grid.get((x, y))
        tile_element = tile_elements.get((x, y))

        if not tile_info or tile_info.get('type') in IMPASSABLE_TILE_TYPES:
            return False

        if tile_element is not None:
            is_collected_item = False
            is_obstacle = False
            for marker in tile_element.findall('Marker'):
                if marker.get('emoji') == COLLECTED_ITEM_EMOJI:
                    is_collected_item = True
                    break
                if marker.get('emoji') in OBSTACLE_MARKER_EMOJIS:
                    is_obstacle = True

            if is_obstacle and not is_collected_item:
                 return False

            if tile_element.get('has-object') == 'true' and not is_collected_item:
                return False

        return True

    destination_tiles = set()
    if is_passable(target_x, target_y):
        destination_tiles.add((target_x, target_y))
    else:
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if is_passable(adj_x, adj_y):
                destination_tiles.add((adj_x, adj_y))

    if not destination_tiles:
        print(json.dumps([]))
        return

    queue = deque([[(start_x, start_y)]])
    visited = {(start_x, start_y)}

    while queue:
        path = queue.popleft()
        current_x, current_y = path[-1]

        if (current_x, current_y) in destination_tiles:
            print(json.dumps(path))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = current_x + dx, current_y + dy
            neighbor_coord = (next_x, next_y)

            if neighbor_coord in visited:
                continue

            if not is_passable(next_x, next_y):
                continue

            can_move = True
            current_type = grid.get((current_x, current_y), {}).get('type')
            neighbor_type = grid.get(neighbor_coord, {}).get('type')

            if neighbor_type in ONE_WAY_RULES:
                allowed_entry_dx, allowed_entry_dy = ONE_WAY_RULES[neighbor_type]
                if (dx, dy) != (allowed_entry_dx, allowed_entry_dy):
                    can_move = False

            if current_type in ONE_WAY_RULES:
                allowed_exit_dx, allowed_exit_dy = ONE_WAY_RULES[current_type]
                if (dx, dy) != (allowed_exit_dx, allowed_exit_dy):
                    can_move = False

            if can_move:
                new_path = list(path)
                new_path.append(neighbor_coord)
                visited.add(neighbor_coord)
                queue.append(new_path)

    print(json.dumps([]))

