import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path():
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        start_x = input_data.get('start_x')
        start_y = input_data.get('start_y')
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])
        can_surf = bool(input_data.get('can_surf', False))

        if start_x is None or start_y is None:
            for row in root.findall('Row'):
                y = int(row.attrib['id'])
                for tile in row.findall('Tile'):
                    if tile.find('Player') is not None or tile.attrib.get('has-player') == 'true':
                        start_x = int(tile.attrib['id'])
                        start_y = y
                        break
                if start_x is not None: break

        if start_x is None:
            print(json.dumps([]))
            return

        grid = {}
        warps = set()
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                has_object = tile.attrib.get('has-object') == 'true'
                is_warp = tile.attrib.get('is-warp') == 'true'

                passable_types = ['FLOOR', 'TALL_GRASS', 'CAVE', 'GRASS', 'SAND', 'STAIRS', 'DOCK', 'WARP', 'STAIRCASE', 'ICE', 'WHIRLPOOL', 'WATER', 'DIRT', 'PATH', 'DOOR']
                is_blocked = (t_type not in passable_types)

                if has_object:
                    if tile.find('Player') is None and tile.attrib.get('has-player') != 'true':
                        is_blocked = True

                if t_type == 'WATER' and not can_surf: is_blocked = True
                if t_type == 'WHIRLPOOL': is_blocked = True

                grid[(x, y)] = is_blocked
                if is_warp: warps.add((x, y))

        queue = deque([(start_x, start_y, [])])
        visited = set([(start_x, start_y)])
        while queue:
            x, y, path = queue.popleft()
            if x == end_x and y == end_y:
                print(json.dumps(path))
                return
            if (x, y) in warps and (x != start_x or y != start_y): continue
            for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                    if not grid.get((nx, ny), True) or (nx == end_x and ny == end_y):
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [btn]))
        print(json.dumps([]))
    except Exception:
        print(json.dumps([]))

find_path()
