import xml.etree.ElementTree as ET
import json
import collections

def solve():
    try:
        # input_data is a global dictionary provided by the harness
        auto = input_data.get('autopress_buttons', False)
        px = int(input_data.get('player_x', 0))
        py = int(input_data.get('player_y', 0))
    except:
        auto = False
        px, py = 0, 0

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    # If px, py are 0, try to find player in XML
    if px == 0 and py == 0:
        found_player = False
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    px, py = int(tile.get('id')), y
                    found_player = True
                    break
            if found_player: break

    # Map tile types and seen status
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tiles[(x, y)] = {
                'type': tile.get('type'),
                'seen': tile.get('seen') == 'true',
                'has_obj': tile.get('has-object') == 'true'
            }

    # Define traversable tiles based on notepad verification
    traversable = {'FLOOR', 'WARP_CARPET_DOWN', 'WARP_CARPET', 'LADDER', 'STAIRS', 'DOOR', 'FLOOR_UP_WALL', 'GRASS', 'TALL_GRASS'}

    # BFS to find reachable tiles
    reachable = set()
    queue = collections.deque([(px, py)])
    reachable.add((px, py))
    parent = {}

    while queue:
        cx, cy = queue.popleft()
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in tiles and (nx, ny) not in reachable:
                tile = tiles[(nx, ny)]
                if tile['type'] in traversable and not tile['has_obj']:
                    reachable.add((nx, ny))
                    parent[(nx, ny)] = (cx, cy)
                    queue.append((nx, ny))

    # Identify unseen tiles adjacent to reachable tiles
    unseen_targets = []
    for rx, ry in reachable:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = rx + dx, ry + dy
            if (nx, ny) in tiles:
                if not tiles[(nx, ny)]['seen']:
                    unseen_targets.append((nx, ny, rx, ry))
            elif 0 <= nx < width and 0 <= ny < height:
                # Boundary tile not in 'tiles' dict implies unseen
                unseen_targets.append((nx, ny, rx, ry))

    if not auto:
        # Return coordinates of unseen tiles
        unique_unseen = []
        seen_coords = set()
        for ux, uy, rx, ry in unseen_targets:
            if (ux, uy) not in seen_coords:
                unique_unseen.append({"x": ux, "y": uy})
                seen_coords.add((ux, uy))
        print(json.dumps(unique_unseen))
    else:
        # Return buttons to reach the closest reachable tile adjacent to an unseen one
        if not unseen_targets:
            print("[]")
            return

        # Dijkstra-like BFS for distance
        dist = {(px, py): 0}
        q = collections.deque([(px, py)])
        while q:
            cx, cy = q.popleft()
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) in reachable and (nx, ny) not in dist:
                    dist[(nx, ny)] = dist[(cx, cy)] + 1
                    q.append((nx, ny))

        # Find best target (closest reachable tile with unseen neighbor)
        best_target = None
        min_dist = float('inf')
        for ux, uy, rx, ry in unseen_targets:
            if (rx, ry) in dist and dist[(rx, ry)] < min_dist:
                min_dist = dist[(rx, ry)]
                best_target = (rx, ry)

        if best_target:
            # Reconstruct path
            path = []
            curr = best_target
            while curr != (px, py):
                path.append(curr)
                curr = parent[curr]
            path.reverse()

            # Convert to buttons
            buttons = []
            cx, cy = px, py
            for nx, ny in path:
                if nx > cx: buttons.append("Right")
                elif nx < cx: buttons.append("Left")
                elif ny > cy: buttons.append("Down")
                elif ny < cy: buttons.append("Up")
                cx, cy = nx, ny
            print(json.dumps(buttons))
        else:
            print("[]")

if __name__ == "__main__":
    solve()

