import json

def solve():
    try:
        target_index = int(input_data['target_index'])
        current_index = int(input_data['current_index'])

        buttons = []
        diff = target_index - current_index
        if diff > 0:
            for _ in range(diff):
                buttons.append("Down")
        elif diff < 0:
            for _ in range(abs(diff)):
                buttons.append("Up")

        buttons.append("A") # Select Pokemon
        buttons.append("A") # Choose 'SHIFT' / 'SWITCH'
        return buttons
    except Exception:
        return []

print(json.dumps(solve()))

