import xml.etree.ElementTree as ET
from collections import deque
import json

def solve():
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])

        root = ET.fromstring(map_xml_string)

        start_x, start_y = -1, -1
        for row in root.findall('Row'):
            curr_y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    start_x = int(tile.attrib['id'])
                    start_y = curr_y
                    break
            if start_x != -1: break

        if start_x == -1: return []

        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                is_wall = t_type in ['WALL', 'COUNTER', 'BOOKSHELF', 'TABLE', 'MART_SHELF']
                obj = tile.find('Object')
                if obj is not None:
                    # NPCs and items are walls unless they are the target (for interaction)
                    if not (x == start_x and y == start_y) and not (x == target_x and y == target_y):
                        is_wall = True
                grid[(x, y)] = not is_wall

        queue = deque([(start_x, start_y, [])])
        visited = {(start_x, start_y)}
        while queue:
            x, y, path = queue.popleft()
            if x == target_x and y == target_y:
                return path
            for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = x + dx, y + dy
                if (nx, ny) in grid and grid[(nx, ny)] and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
        return []
    except Exception:
        return []

print(json.dumps(solve()))
