import xml.etree.ElementTree as ET
import heapq
import json

def is_traversable(x, y, tile_map, has_surf, impassable_coords_set):
    if not (1 <= x <= tile_map['width'] and 1 <= y <= tile_map['height']):
        return False
    coord_str = f"({x}, {y})"
    if coord_str in impassable_coords_set:
        return False
    tile = tile_map['tiles'].get(coord_str)
    if not tile or tile.get('type') == 'unknown':
        return False

    tile_type = tile.get('type')
    has_object = tile.get('has_object', 'false') == 'true'
    object_id_name = tile.get('object_id_name', '')

    if has_object and object_id_name != 'Pikachu' and object_id_name != 'Guard':
        return False

    traversable_types = ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'open_gate', 'gate_offscreen', 'teleport', 'hole', 'ladder_up', 'ladder_down']
    if has_surf:
        traversable_types.append('water')

    if tile_type in traversable_types:
        return True

    if tile_type == 'ledge':
        return True

    return False

def pathfinder_tool(target_x, target_y, party_data, impassable_coords_str):
    try:
        party = json.loads(party_data)
        has_surf = any('SURF' in move for pokemon in party for move in pokemon.get('moves', []))

        impassable_coords_set = set(json.loads(impassable_coords_str)) if impassable_coords_str and impassable_coords_str != 'null' else set()

        root = ET.fromstring(map_xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))

        tile_map = {'width': map_width, 'height': map_height, 'tiles': {}}
        player_pos = None

        for row_elem in root.findall('Row'):
            for tile_elem in row_elem.findall('Tile'):
                coord = tile_elem.get('coordinate')
                tile_data = {
                    'type': tile_elem.get('type'),
                    'seen': tile_elem.get('seen'),
                    'has_object': 'true' if tile_elem.find('Object') is not None else 'false'
                }
                if tile_elem.find('Object') is not None:
                    tile_data['object_id_name'] = tile_elem.find('Object').get('id-name')

                if tile_elem.find('Player') is not None:
                    player_pos = tuple(map(int, coord.strip('()').split(',')))
                tile_map['tiles'][coord] = tile_data

        if not player_pos:
            print("Error: Player position not found in map XML.")
            return

        start_node = player_pos
        end_node = (target_x, target_y)

        if not is_traversable(end_node[0], end_node[1], tile_map, has_surf, impassable_coords_set):
            found_adjacent = False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_x, adj_y = end_node[0] + dx, end_node[1] + dy
                if is_traversable(adj_x, adj_y, tile_map, has_surf, impassable_coords_set):
                    end_node = (adj_x, adj_y)
                    found_adjacent = True
                    break
            if not found_adjacent:
                print("Error: Target is not traversable and has no traversable adjacent tiles.")
                return

        open_set = [(0, start_node)]
        came_from = {}
        g_score = {start_node: 0}
        f_score = {start_node: abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])}

        while open_set:
            _, current = heapq.heappop(open_set)

            if current == end_node:
                path = []
                while current in came_from:
                    path.append({'x': current[0], 'y': current[1]})
                    current = came_from[current]
                path.append({'x': start_node[0], 'y': start_node[1]})
                print(json.dumps(path[::-1]))
                return

            neighbors = []
            x, y = current

            current_tile_type = tile_map['tiles'].get(f"({x}, {y})", {}).get('type')

            if current_tile_type == 'ledge':
                neighbors.append((x, y + 1))
            else:
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbors.append((x + dx, y + dy))

            for neighbor in neighbors:
                nx, ny = neighbor
                if not is_traversable(nx, ny, tile_map, has_surf, impassable_coords_set):
                    continue

                neighbor_tile_type = tile_map['tiles'].get(f"({nx}, {ny})", {}).get('type')

                if neighbor_tile_type == 'ledge' and current_tile_type != 'ledge':
                    if ny != y + 1:
                        continue

                current_elevation = tile_map['tiles'].get(f"({x}, {y})", {}).get('type') in ['elevated_ground']
                neighbor_elevation = tile_map['tiles'].get(f"({nx}, {ny})", {}).get('type') in ['elevated_ground']
                if current_elevation != neighbor_elevation:
                    is_current_steps = tile_map['tiles'].get(f"({x}, {y})", {}).get('type') == 'steps'
                    is_neighbor_steps = tile_map['tiles'].get(f"({nx}, {ny})", {}).get('type') == 'steps'
                    if not (is_current_steps or is_neighbor_steps):
                        continue

                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = tentative_g_score + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

        print("Error: No path found.")
    except Exception as e:
        print(f"An error occurred: {e}")

pathfinder_tool(int(input_data['target_x']), int(input_data['target_y']), input_data['party_data'], input_data.get('impassable_coords'))

