import xml.etree.ElementTree as ET
import heapq
import json
from collections import deque

def solve_boulder_puzzle(input_data):
    grid = {}
    player_pos = None
    map_width = 0
    map_height = 0
    party_data = []

    def parse_map_and_party(xml_string, party_str):
        nonlocal grid, player_pos, map_width, map_height, party_data
        root = ET.fromstring(xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                is_warp = tile_elem.get('is-warp') == 'true'
                object_id_name = None
                obj_elem = tile_elem.find('Object')
                if obj_elem is not None:
                    object_id_name = obj_elem.get('id-name')
                if tile_elem.find('Player') is not None:
                    player_pos = (x, y)
                grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'is_warp': is_warp, 'object_id_name': object_id_name}
        party_data = json.loads(party_str) if party_str else []

    def has_hm(hm_name):
        for pokemon in party_data:
            if not pokemon.get('is_fainted', False):
                for move in pokemon.get('moves', []):
                    if isinstance(move, dict) and move.get('name') == hm_name: return True
                    elif isinstance(move, str) and move == hm_name: return True
        return False

    def is_player_traversable(from_coord, to_coord, current_boulders):
        if to_coord not in grid: return False
        to_tile = grid[to_coord]
        from_tile = grid[from_coord]
        from_type = from_tile['type']
        to_type = to_tile['type']

        if to_type in ['impassable', 'boulder_barrier'] or to_coord in current_boulders: return False
        if to_tile['has_object'] and not to_tile['is_warp'] and to_tile.get('object_id_name') != 'PIKACHU': return False

        if to_type == 'water' and has_hm('SURF'):
            if from_type in ['ground', 'steps', 'grass', 'water']:
                return True
        if from_type == 'water' and to_type in ['ground', 'steps', 'grass'] and has_hm('SURF'):
            return True

        valid_land_types = {'ground', 'grass', 'steps', 'elevated_ground', 'ladder_up', 'ladder_down'}
        if from_type in valid_land_types and to_type in valid_land_types:
            if from_type == 'steps' and to_type in {'ground', 'grass', 'elevated_ground', 'steps'}: return True
            if to_type == 'steps' and from_type in {'ground', 'grass', 'elevated_ground'}: return True
            if from_type == to_type: return True
            if from_type in {'ground', 'grass'} and to_type in {'ground', 'grass'}: return True
            if from_type in {'ladder_up', 'ladder_down'} or to_type in {'ladder_up', 'ladder_down'}: return True
            return False
        return False

    def get_player_neighbors(node, current_boulders):
        x, y = node
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= map_width and 1 <= ny <= map_height:
                if is_player_traversable((x, y), (nx, ny), current_boulders):
                    neighbors.append((nx, ny))
        return neighbors

    def player_a_star(start, goal, current_boulders):
        frontier = [(0, start)]
        came_from = {start: None}
        cost_so_far = {start: 0}
        max_iterations = 2000
        iterations = 0
        while frontier:
            iterations += 1
            if iterations > max_iterations: return None
            _, current = heapq.heappop(frontier)
            if current == goal: break
            for next_node in get_player_neighbors(current, current_boulders):
                new_cost = cost_so_far[current] + 1
                if next_node not in cost_so_far or new_cost < cost_so_far[next_node]:
                    cost_so_far[next_node] = new_cost
                    priority = new_cost + abs(goal[0] - next_node[0]) + abs(goal[1] - next_node[1])
                    heapq.heappush(frontier, (priority, next_node))
                    came_from[next_node] = current
        else: return None
        path = []
        current = goal
        while current is not None: path.append(current); current = came_from.get(current)
        path.reverse()
        return path

    def get_boulder_neighbors(state, current_player_pos):
        neighbors = []
        boulders = set(state)
        for i, boulder in enumerate(state):
            bx, by = boulder
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                player_pos_req = (bx - dx, by - dy)
                new_boulder_pos = (bx + dx, by + dy)
                if new_boulder_pos in boulders or new_boulder_pos not in grid or grid[new_boulder_pos]['type'] in ['impassable', 'boulder_barrier'] or (grid[new_boulder_pos]['has_object'] and not grid[new_boulder_pos]['is_warp'] and grid[new_boulder_pos].get('object_id_name') != 'PIKACHU'): continue
                path_to_push = player_a_star(current_player_pos, player_pos_req, boulders)
                if path_to_push:
                    new_state_list = list(state)
                    new_state_list[i] = new_boulder_pos
                    new_state_tuple = tuple(sorted(new_state_list))
                    move = {'boulder_index': i, 'start_pos': boulder, 'end_pos': new_boulder_pos, 'player_path': path_to_push}
                    neighbors.append((new_state_tuple, move))
        return neighbors

    def puzzle_heuristic(state, switches):
        total_dist = 0
        unplaced_boulders = sorted([b for b in state if b not in switches])
        open_switches = sorted([s for s in switches if s not in state])
        for boulder in unplaced_boulders:
            if not open_switches: break
            min_dist = float('inf')
            best_switch = None
            for switch in open_switches:
                dist = abs(boulder[0] - switch[0]) + abs(boulder[1] - switch[1])
                if dist < min_dist: min_dist = dist; best_switch = switch
            total_dist += min_dist
            if best_switch: open_switches.remove(best_switch)
        return total_dist

    def puzzle_a_star_solver(start_state, switches, initial_player_pos):
        start_node = (tuple(sorted(start_state)), initial_player_pos)
        frontier = [(puzzle_heuristic(start_node[0], switches), start_node)]
        came_from = {start_node: (None, None)}
        cost_so_far = {start_node: 0}
        max_iterations = 15000
        iterations = 0
        while frontier:
            iterations += 1
            if iterations > max_iterations: return 'timeout'
            _, current_node = heapq.heappop(frontier)
            current_state, current_player_pos = current_node
            if len(switches) > 0 and set(switches).issubset(set(current_state)):
                path = []
                curr = current_node
                while curr is not None:
                    parent, move = came_from.get(curr, (None, None))
                    if move: path.append(move)
                    curr = parent
                path.reverse()
                return path
            for next_state, move in get_boulder_neighbors(current_state, current_player_pos):
                new_cost = cost_so_far[current_node] + 1
                next_player_pos = move['start_pos']
                next_node = (next_state, next_player_pos)
                if next_node not in cost_so_far or new_cost < cost_so_far[next_node]:
                    cost_so_far[next_node] = new_cost
                    priority = new_cost + puzzle_heuristic(next_state, switches)
                    heapq.heappush(frontier, (priority, next_node))
                    came_from[next_node] = (current_node, move)
        return None

    try:
        parse_map_and_party(map_xml_string, input_data['party_data'])
        boulder_coords = [tuple(coord) for coord in json.loads(input_data['boulder_coords'])]
        switch_coords = [tuple(coord) for coord in json.loads(input_data['switch_coords'])]
        solution = puzzle_a_star_solver(boulder_coords, switch_coords, player_pos)
        if solution == 'timeout':
            print(json.dumps({'error': 'Solver timed out after too many iterations.'}))
        elif solution:
            print(json.dumps({'solution_path': solution}))
        else:
            print(json.dumps({'error': 'No solution found for the puzzle.'}))
    except Exception as e:
        import traceback
        print(json.dumps({'error': f'An error occurred: {str(e)}', 'traceback': traceback.format_exc()}))

solve_boulder_puzzle(input_data)

