import xml.etree.ElementTree as ET
import json
import sys

def find_closest_unseen_tile(map_xml_string, input_data):
    try:
        party_data_str = input_data.get('party_data', '[]')
        party = json.loads(party_data_str)

        def parse_map(xml_string):
            root = ET.fromstring(xml_string)
            grid = {}
            player_pos = None
            unseen_tiles = []
            for row_elem in root.findall('Row'):
                y = int(row_elem.get('id'))
                for tile_elem in row_elem.findall('Tile'):
                    x = int(tile_elem.get('id'))
                    tile_type = tile_elem.get('type')
                    seen = tile_elem.get('seen') == 'true'
                    has_object = tile_elem.get('has-object') == 'true'
                    is_warp = tile_elem.get('is-warp') == 'true'
                    object_id_name = None
                    obj_elem = tile_elem.find('Object')
                    if obj_elem is not None:
                        object_id_name = obj_elem.get('id-name')
                    if tile_elem.find('Player') is not None:
                        player_pos = (x, y)
                    grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'is_warp': is_warp, 'object_id_name': object_id_name, 'seen': seen}
                    if not seen:
                        unseen_tiles.append((x, y))
            return grid, player_pos, unseen_tiles, int(root.get('width')), int(root.get('height'))

        def has_hm(party, hm_name):
            if party is None: return False
            for pokemon in party:
                for move in pokemon.get('moves', []):
                    if isinstance(move, str) and hm_name.upper() in move.upper(): return True
            return False

        def is_traversable(from_coord, to_coord, grid, party):
            from_tile = grid.get(from_coord)
            to_tile = grid.get(to_coord)
            if not from_tile or not to_tile: return False
            from_type, to_type = from_tile['type'], to_tile['type']

            if to_type in ['impassable', 'unknown']: return False

            if to_tile['has_object'] and not to_tile['is_warp']:
                object_name = to_tile.get('object_id_name')
                if object_name not in ['PIKACHU', 'Guard']: return False

            if to_type == 'ledge': return from_coord[1] == to_coord[1] - 1

            # Handle water traversal
            if from_type == 'water' or to_type == 'water':
                if from_type == 'water' and to_type == 'water': return True
                if from_type == 'water' and to_type in ['ground', 'steps', 'grass']: return True
                if to_type == 'water' and from_type in ['ground', 'steps', 'grass']:
                    return has_hm(party, 'SURF')
                return False

            # Handle land traversal
            walkable_land = {'ground', 'grass', 'elevated_ground', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen'}
            if from_type not in walkable_land or to_type not in walkable_land: return False

            is_from_elevated = from_type == 'elevated_ground'
            is_to_elevated = to_type == 'elevated_ground'

            if is_from_elevated == is_to_elevated: return True

            if from_type == 'steps' or to_type == 'steps': return True

            return False

        def get_neighbors(node, grid, party, map_width, map_height):
            x, y = node
            neighbors = []
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if 1 <= nx <= map_width and 1 <= ny <= map_height:
                    if is_traversable((x, y), (nx, ny), grid, party):
                        neighbors.append((nx, ny))
            return neighbors

        def bfs_find_reachable(start, grid, party, map_width, map_height):
            frontier = [start]
            reachable = {start}
            while frontier:
                current = frontier.pop(0)
                for neighbor in get_neighbors(current, grid, party, map_width, map_height):
                    if neighbor not in reachable:
                        reachable.add(neighbor)
                        frontier.append(neighbor)
            return reachable

        print('DEBUG: Starting tool execution.', file=sys.stderr)
        grid, player_pos, unseen_tiles, map_width, map_height = parse_map(map_xml_string)
        if player_pos is None: raise ValueError('Player position not found.')
        print(f'DEBUG: Player at {player_pos}, Found {len(unseen_tiles)} total unseen tiles.', file=sys.stderr)

        reachable_tiles = bfs_find_reachable(player_pos, grid, party, map_width, map_height)
        print(f'DEBUG: BFS found {len(reachable_tiles)} reachable tiles.', file=sys.stderr)

        reachable_unseen = [tile for tile in unseen_tiles if tile in reachable_tiles]
        print(f'DEBUG: Found {len(reachable_unseen)} reachable unseen tiles.', file=sys.stderr)

        if not reachable_unseen:
            print(json.dumps({'message': 'No reachable unseen tiles found.'}))
            return

        closest_tile = min(reachable_unseen, key=lambda t: abs(t[0] - player_pos[0]) + abs(t[1] - player_pos[1]))
        print(f'DEBUG: Closest unseen tile is {closest_tile}.', file=sys.stderr)

        for dx, dy in sorted([(0, 1), (0, -1), (1, 0), (-1, 0)], key=lambda p: (abs((closest_tile[0] + p[0]) - player_pos[0]) + abs((closest_tile[1] + p[1]) - player_pos[1]))):
            adj_tile = (closest_tile[0] + dx, closest_tile[1] + dy)
            if adj_tile in reachable_tiles and grid[adj_tile]['type'] != 'unknown':
                print(json.dumps({'closest_reachable_unseen_tile': {'x': closest_tile[0], 'y': closest_tile[1]}, 'adjacent_tile': {'x': adj_tile[0], 'y': adj_tile[1]}}))
                return

        print(json.dumps({'message': 'Found a reachable unseen tile, but no reachable adjacent tile to path to.'}))

    except Exception as e:
        print(json.dumps({'error': str(e)}))
