import json
from collections import deque

def solve():
    # Clean grid
    grid_str = [
        "WWWWWWWWWW",
        "..........",
        "WW..W.W...",
        "....W.W.O.",
        "....W.WWWW",
        "..O.W.....",
        "....W.WW..",
        ".L..W...O.",
        "..WWW...W.",
        "....W..LW.",
        "..W.WW.W..",
        "..W....W..",
        "..W.WW...W",
        "..W......W",
        "..W....W.W",
        "..W....W.W",
        "..W..W...W",
        "..W..W...W"
    ]
    grid = {}
    pits = set()
    ladders = set()
    for y, line in enumerate(grid_str):
        for x, char in enumerate(line):
            grid[(x, y)] = char
            if char == 'O': pits.add((x, y))
            if char == 'L': ladders.add((x, y))

    npcs = {(4, 1), (4, 11), (1, 15), (6, 8), (9, 2)}
    # Initial positions after reset
    boulders = ((3, 3), (6, 1), (8, 14))
    player_start = (int(input_data['player_x']), int(input_data['player_y']))

    def get_reachable(p, b):
        reach = set()
        q = deque([p])
        vis = {p}
        while q:
            curr = q.popleft()
            reach.add(curr)
            # Ladder teleport
            if grid.get(curr) == 'L':
                for l in ladders:
                    if l not in vis and l not in b and l not in npcs:
                        vis.add(l); q.append(l)
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nxt = (curr[0] + dx, curr[1] + dy)
                if nxt in grid and grid[nxt] in ['.', 'L'] and nxt not in b and nxt not in npcs and nxt not in vis:
                    vis.add(nxt); q.append(nxt)
        return reach

    # BFS state: (tuple_of_boulder_positions, player_pos_canonical)
    start_reach = get_reachable(player_start, boulders)
    if not start_reach: return {"status": "blocked_start"}

    start_state = (boulders, min(start_reach))
    q = deque([(boulders, player_start, [])])
    vis = {start_state}

    iters = 0
    while q:
        cb, cp, path = q.popleft()
        iters += 1
        if iters > 30000: break

        reach = get_reachable(cp, cb)

        for b_idx, b_pos in enumerate(cb):
            others = tuple(x for j, x in enumerate(cb) if b_idx != j)
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                pf = (b_pos[0] - dx, b_pos[1] - dy)
                pt = (b_pos[0] + dx, b_pos[1] + dy)

                if pf in reach:
                    if pt in pits:
                        return {
                            "status": "found",
                            "boulder_idx": b_idx,
                            "start_pos": boulders[b_idx],
                            "final_push": {"from": pf, "to": pt},
                            "pushes": path + [{"from": pf, "to": pt}]
                        }

                    if pt in grid and grid[pt] == '.' and pt not in others and pt not in npcs:
                        nb = list(cb)
                        nb[b_idx] = pt
                        nbt = tuple(sorted(nb))
                        # New player pos is the old boulder pos
                        nr = get_reachable(b_pos, nbt)
                        if nr:
                            ns = (nbt, min(nr))
                            if ns not in vis:
                                vis.add(ns)
                                q.append((nbt, b_pos, path + [{"from": pf, "to": pt}]))

    return {"status": "none", "iters": iters}

print(json.dumps(solve()))
