import xml.etree.ElementTree as ET
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        target = input_data.get('object_name', 'BOULDER').upper()
        found = []
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                obj = tile.find('Object')
                if obj is not None:
                    name = obj.attrib.get('id-name', '').upper()
                    if target in name:
                        found.append({
                            'x': x, 
                            'y': y, 
                            'id': int(obj.attrib.get('id', 0)), 
                            'name': name
                        })
        print(json.dumps(found))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

run()

analyze_gym_map_v2.python_script:
import xml.etree.ElementTree as ET
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        map_id = input_data.get('map_id')
        target_map = root if root.get('id') == map_id else root.find(f".//Map[@id='{map_id}']")

        if target_map is None:
            print(json.dumps({"error": f"Map {map_id} not found."}))
            return

        boulders = []
        pits = []
        warps = []

        for row in target_map.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                t_type = tile.get('type')

                if t_type == 'PIT':
                    pits.append({'x': x, 'y': y})

                if tile.get('is-warp') == 'true':
                    warp_elem = tile.find('Warp')
                    warps.append({
                        'x': x, 'y': y,
                        'entry_point': warp_elem.get('entry_point') if warp_elem is not None else None
                    })

                for obj in tile.findall('Object'):
                    name = obj.get('id-name', '').upper()
                    if 'BOULDER' in name:
                        boulders.append({
                            'x': x, 'y': y,
                            'id': int(obj.get('id', 0)),
                            'name': name
                        })

        print(json.dumps({
            "map_id": map_id,
            "boulders": boulders,
            "pits": pits,
            "warps": warps
        }, indent=2))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

run()

