        width = int(root.get('width'))
        height = int(root.get('height'))

        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])

        passable_tiles = set()
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                tile_type = tile.get('type')
                has_object = tile.get('has-object') == 'true'
                # Treat unseen tiles as potentially passable for pathfinding
                # Row 0 is marked as WALL but might be passable
                if tile_type in ['FLOOR', 'PIT', 'LADDER', 'ICE'] and not has_object:
                    passable_tiles.add((x, y))
                elif tile.get('seen') == 'false':
                    passable_tiles.add((x, y))

        # Ensure start and end are considered passable for BFS
        passable_tiles.add((start_x, start_y))
        passable_tiles.add((end_x, end_y))

        queue = deque([(start_x, start_y, [])])
        visited = set([(start_x, start_y)])

        while queue:
            x, y, path = queue.popleft()
            if x == end_x and y == end_y:
                full_path = [{"x": start_x, "y": start_y}] + path
                if input_data.get('autopress_buttons'):
                    buttons = []
                    curr_x, curr_y = start_x, start_y
                    for step in path:
                        if step['x'] > curr_x: buttons.append('Right')
                        elif step['x'] < curr_x: buttons.append('Left')
                        elif step['y'] > curr_y: buttons.append('Down')
                        elif step['y'] < curr_y: buttons.append('Up')
                        curr_x, curr_y = step['x'], step['y']
                    print(json.dumps(buttons))
                else:
                    print(json.dumps(full_path))
                return

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < width and 0 <= ny < height and (nx, ny) in passable_tiles and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{"x": nx, "y": ny}]))

        print(json.dumps({"error": "No path found"}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

