solve_gym_puzzle_v3.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.get('width'))
        height = int(root.get('height'))

        passable = set()
        pits = set()
        boulders = {}

        # Identify passable tiles and on-screen boulders
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                t_type = tile.get('type')
                if t_type in ['FLOOR', 'LADDER', 'ICE']:
                    has_obj = tile.get('has-object') == 'true'
                    if has_obj:
                        obj = tile.find('Object')
                        if obj is not None and obj.get('id-name') == 'BOULDER':
                            boulders[int(obj.get('id'))] = (x, y)
                        else:
                            continue
                    passable.add((x, y))
                elif t_type == 'PIT':
                    pits.add((x, y))

        # Include off-screen boulders from markers
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                for marker in tile.findall('Marker'):
                    if 'Boulder' in marker.text:
                        bid = int(marker.get('object_id'))
                        if bid not in boulders:
                            boulders[bid] = (x, y)
                            passable.add((x, y))

        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])

        def solve_one(b_id, current_boulders, p_pos):
            bx, by = current_boulders[b_id]
            others = {pos for id, pos in current_boulders.items() if id != b_id}
            q = deque([(bx, by, p_pos[0], p_pos[1], [])])
            v = set([(bx, by, p_pos[0], p_pos[1])])
            while q:
                cbx, cby, cpx, cpy, path = q.popleft()
                if (cbx, cby) in pits: return path
                if len(path) > 250: continue
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    npx, npy = cpx + dx, cpy + dy
                    if 0 <= npx < width and 0 <= npy < height and (npx, npy) in passable and (npx, npy) not in others:
                        if (npx, npy) == (cbx, cby):
                            nbx, nby = cbx + dx, cby + dy
                            if 0 <= nbx < width and 0 <= nby < height and ((nbx, nby) in passable or (nbx, nby) in pits) and (nbx, nby) not in others:
                                if (nbx, nby, npx, npy) not in v:
                                    v.add((nbx, nby, npx, npy))
                                    btn = 'Up' if dy==-1 else 'Down' if dy==1 else 'Left' if dx==-1 else 'Right'
                                    q.append((nbx, nby, npx, npy, path + [btn]))
                        else:
                            if (cbx, cby, npx, npy) not in v:
                                v.add((cbx, cby, npx, npy))
                                btn = 'Up' if dy==-1 else 'Down' if dy==1 else 'Left' if dx==-1 else 'Right'
                                q.append((cbx, cby, npx, npy, path + [btn]))
            return None

        # Try to solve for each boulder in order
        for b_id in sorted(boulders.keys()):
            path = solve_one(b_id, boulders, (start_x, start_y))
            if path:
                print(json.dumps(path))
                return
        print(json.dumps({"error": "No solution found"}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

solve()
