import xml.etree.ElementTree as ET
import json
from collections import deque

def create_grid(map_xml_string):
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_type = tile_elem.get('type')
            obj_elem = tile_elem.find('Object')
            has_impassable_object = False
            if obj_elem is not None:
                obj_id_name = obj_elem.get('id-name', '')
                if obj_id_name not in ['Pikachu', 'Unknown']:
                    has_impassable_object = True

            has_boulder = tile_elem.find('Boulder') is not None
            row.append({'type': tile_type, 'has_impassable_object': has_impassable_object, 'has_boulder': has_boulder})
        grid.append(row)
    return grid, width, height

def bfs(start_node, grid, visited, landmass_id, width, height):
    q = deque([start_node])
    visited[start_node] = landmass_id

    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down', 'water'}
    elevated_types = {'elevated_ground', 'cleared_boulder_barrier'}

    while q:
        x, y = q.popleft()
        current_tile = grid[y-1][x-1]
        current_type = current_tile['type']

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            if (nx, ny) in visited:
                continue

            neighbor_tile = grid[ny-1][nx-1]
            neighbor_type = neighbor_tile['type']

            if neighbor_type not in walkable_types and neighbor_type != 'ledge' or neighbor_tile['has_impassable_object'] or neighbor_tile['has_boulder']:
                continue

            move_is_valid = False

            if neighbor_type == 'ledge':
                if dy == 1: # Moving down from current
                    nnx, nny = nx, ny + 1
                    if 1 <= nnx <= width and 1 <= nny <= height and (nnx, nny) not in visited:
                        landing_tile = grid[nny-1][nnx-1]
                        if landing_tile['type'] in walkable_types and not landing_tile['has_impassable_object'] and not landing_tile['has_boulder']:
                            visited[(nnx, nny)] = landmass_id
                            q.append((nnx, nny))
                continue
            else:
                current_is_elevated = current_type in elevated_types
                neighbor_is_elevated = neighbor_type in elevated_types

                if current_is_elevated == neighbor_is_elevated:
                    move_is_valid = True
                elif (current_type == 'steps' or neighbor_type == 'steps'):
                    move_is_valid = True
                elif not current_is_elevated and neighbor_type == 'cleared_boulder_barrier':
                    move_is_valid = True

            if move_is_valid:
                visited[(nx, ny)] = landmass_id
                q.append((nx, ny))

try:
    grid, width, height = create_grid(map_xml_string)
    visited = {}
    landmass_id_counter = 1
    landmasses = {}

    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down', 'water', 'ledge'}

    for y in range(1, height + 1):
        for x in range(1, width + 1):
            if (x, y) not in visited:
                tile = grid[y-1][x-1]
                if tile['type'] in walkable_types and not tile['has_impassable_object']:
                    bfs((x, y), grid, visited, landmass_id_counter, width, height)
                    landmass_id_counter += 1

    for (x, y), landmass_id in visited.items():
        if landmass_id not in landmasses:
            landmasses[landmass_id] = []
        landmasses[landmass_id].append((x, y))

    output = {
        "num_landmasses": len(landmasses),
        "landmass_report": []
    }

    for lm_id, coords in landmasses.items():
        output["landmass_report"].append({
            "landmass_id": lm_id,
            "num_tiles": len(coords)
        })

    start_x = input_data.get('start_x')
    start_y = input_data.get('start_y')
    end_x = input_data.get('end_x')
    end_y = input_data.get('end_y')

    if start_x and start_y:
        start_coord = (int(start_x), int(start_y))
        output["start_coord_landmass_id"] = visited.get(start_coord, "N/A or impassable")

    if end_x and end_y:
        end_coord = (int(end_x), int(end_y))
        output["end_coord_landmass_id"] = visited.get(end_coord, "N/A or impassable")

    print(json.dumps(output, indent=2))

except Exception as e:
    print(json.dumps({"error": str(e)}))

