import xml.etree.ElementTree as ET
import heapq
import json
import sys

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(node, grid, width, height, ignore_object_ids):
    x, y = node
    neighbors = []
    impassable_types = {'impassable', 'unknown', 'boulder_barrier'}
    all_walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}

    current_tile = grid[y - 1][x - 1]
    current_type = current_tile['type']
    print(f"DEBUG: Pathfinding from node ({x},{y}) (type: {current_type})")

    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy

        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny - 1][nx - 1]
        neighbor_type = neighbor_tile['type']
        print(f"  - Checking neighbor ({nx},{ny}) (type: {neighbor_type})")

        if neighbor_tile['has_boulder']:
            print(f"    - REJECTED: Has boulder")
            continue

        if neighbor_type == 'ledge':
            if dy == 1:
                landing_x, landing_y = nx, ny + 1
                if 1 <= landing_y <= height:
                    landing_tile = grid[landing_y - 1][landing_x - 1]
                    if landing_tile['type'] not in impassable_types and not landing_tile['has_impassable_object'] and not landing_tile['has_boulder']:
                        print("    - ACCEPTED (Ledge Hop)")
                        neighbors.append((landing_x, landing_y))
            continue

        if neighbor_type in impassable_types:
            print(f"    - REJECTED: Impassable type '{neighbor_type}'")
            continue

        obj_id = neighbor_tile['object_id']
        if neighbor_tile['has_impassable_object'] and (not obj_id or obj_id not in ignore_object_ids):
            print(f"    - REJECTED: Impassable object ID {obj_id}")
            continue

        is_elevation_change = {current_type, neighbor_type} in [{'ground', 'elevated_ground'}, {'grass', 'elevated_ground'}]
        uses_steps = current_type == 'steps' or neighbor_type == 'steps'
        if is_elevation_change and not uses_steps:
            print("    - REJECTED: Invalid elevation change without steps")
            continue

        if neighbor_type in all_walkable_types:
            print("    - ACCEPTED")
            neighbors.append((nx, ny))
        else:
            print(f"    - REJECTED: Not in walkable types")

    return neighbors

def a_star_search(start, end, grid, width, height, ignore_object_ids):
    start_x, start_y = start
    end_x, end_y = end

    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        for neighbor in get_neighbors(current, grid, width, height, ignore_object_ids):
            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    ignore_object_ids_str = input_data.get('ignore_object_ids', '')
    ignore_object_ids = [int(id_str.strip()) for id_str in ignore_object_ids_str.split(',') if id_str.strip()]

    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_type = tile_elem.get('type')
            obj_elem = tile_elem.find('Object')
            has_impassable_object = False
            obj_id = None
            if obj_elem is not None:
                obj_id_name = obj_elem.get('id-name', '')
                if obj_id_name not in ['Pikachu', 'Unknown']:
                    has_impassable_object = True
                    obj_id_str = obj_elem.get('id')
                    if obj_id_str:
                        obj_id = int(obj_id_str)

            has_boulder = tile_elem.find('Boulder') is not None
            row.append({'type': tile_type, 'has_impassable_object': has_impassable_object, 'has_boulder': has_boulder, 'object_id': obj_id})
        grid.append(row)

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    path = a_star_search(start_node, end_node, grid, width, height, ignore_object_ids)

    if path:
        path_coords = [{'x': x, 'y': y} for x, y in path]
        print(json.dumps(path_coords))
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = end_x + dx, end_y + dy
            if 1 <= adj_x <= width and 1 <= adj_y <= height:
                adj_tile = grid[adj_y - 1][adj_x - 1]
                if adj_tile['type'] not in {'impassable', 'unknown'} and not adj_tile['has_impassable_object'] and not adj_tile['has_boulder']:
                    path = a_star_search(start_node, (adj_x, adj_y), grid, width, height, ignore_object_ids)
                    if path:
                        path_coords = [{'x': x, 'y': y} for x, y in path]
                        print(json.dumps(path_coords))
                        sys.exit()
        print(json.dumps({"error": "No path found"}))

except Exception as e:
    print(json.dumps({"error": str(e)}))

