import xml.etree.ElementTree as ET
import heapq
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(node, grid, width, height):
    x, y, mode = node
    neighbors = []
    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
    surfable_types = {'water'}
    impassable_types = {'impassable', 'unknown', 'boulder_barrier'}

    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy

        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny-1][nx-1]
        neighbor_type = neighbor_tile['type']

        if neighbor_type in impassable_types or neighbor_tile['has_impassable_object'] or neighbor_tile['has_boulder']:
            continue

        if mode == 'walking':
            if neighbor_type in walkable_types:
                neighbors.append((nx, ny, 'walking'))
            elif neighbor_type in surfable_types:
                # Can transition to surfing from a walkable tile adjacent to water
                current_tile = grid[y-1][x-1]
                if current_tile['type'] in walkable_types:
                    neighbors.append((nx, ny, 'surfing'))
        elif mode == 'surfing':
            if neighbor_type in surfable_types:
                neighbors.append((nx, ny, 'surfing'))
            elif neighbor_type in walkable_types:
                # Can transition to walking from water adjacent to a walkable tile
                neighbors.append((nx, ny, 'walking'))

    return neighbors

def a_star_search(start, end, grid, width, height):
    start_x, start_y = start
    end_x, end_y = end

    start_tile_type = grid[start_y-1][start_x-1]['type']
    initial_mode = 'surfing' if start_tile_type == 'water' else 'walking'
    start_node = (start_x, start_y, initial_mode)

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: heuristic((start_x, start_y), (end_x, end_y))}

    while open_set:
        _, current_node = heapq.heappop(open_set)
        current_x, current_y, current_mode = current_node

        if (current_x, current_y) == (end_x, end_y):
            path = []
            while current_node in came_from:
                path.append((current_node[0], current_node[1]))
                current_node = came_from[current_node]
            path.append((start_x, start_y))
            return path[::-1]

        for neighbor_node in get_neighbors(current_node, grid, width, height):
            tentative_g_score = g_score[current_node] + 1

            if tentative_g_score < g_score.get(neighbor_node, float('inf')):
                came_from[neighbor_node] = current_node
                g_score[neighbor_node] = tentative_g_score
                f_score[neighbor_node] = tentative_g_score + heuristic((neighbor_node[0], neighbor_node[1]), (end_x, end_y))
                if neighbor_node not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor_node], neighbor_node))

    return None

