import xml.etree.ElementTree as ET
import json
import heapq

def player_heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_player_neighbors(p_node, p_grid, p_width, p_height, blocked_tiles):
    x, y = p_node
    neighbors = []
    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if not (1 <= nx <= p_width and 1 <= ny <= p_height):
            continue
        if (nx, ny) in blocked_tiles:
            continue
        neighbor_tile = p_grid[ny - 1][nx - 1]
        if neighbor_tile['type'] in walkable_types and not neighbor_tile['has_impassable_object']:
            current_type = p_grid[y - 1][x - 1]['type']
            neighbor_type = neighbor_tile['type']
            is_elevation_change = {current_type, neighbor_type} in [{'ground', 'elevated_ground'}, {'grass', 'elevated_ground'}]
            uses_steps = current_type == 'steps' or neighbor_type == 'steps'
            if not (is_elevation_change and not uses_steps):
                neighbors.append((nx, ny))
    return neighbors

def find_player_path(p_start, p_end, p_grid, p_width, p_height, blocked_tiles):
    if not (1 <= p_end[0] <= p_width and 1 <= p_end[1] <= p_height) or p_grid[p_end[1]-1][p_end[0]-1]['type'] == 'impassable' or p_end in blocked_tiles:
        return False
    open_set = []
    heapq.heappush(open_set, (0, p_start))
    came_from = {}
    g_score = {p_start: 0}
    while open_set:
        _, current = heapq.heappop(open_set)
        if current == p_end:
            return True
        for neighbor in get_player_neighbors(current, p_grid, p_width, p_height, blocked_tiles):
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                heapq.heappush(open_set, (tentative_g_score + player_heuristic(neighbor, p_end), neighbor))
    return False

def boulder_heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_boulder_push_states(state, grid, width, height, all_boulders):
    boulder_pos, player_pos = state
    bx, by = boulder_pos
    next_states = []
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        push_pos = (bx - dx, by - dy)
        new_boulder_pos = (bx + dx, by + dy)
        if not (1 <= new_boulder_pos[0] <= width and 1 <= new_boulder_pos[1] <= height):
            continue
        new_boulder_tile = grid[new_boulder_pos[1] - 1][new_boulder_pos[0] - 1]
        walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch'}
        other_boulders = all_boulders - {boulder_pos}
        if new_boulder_tile['type'] not in walkable_types or new_boulder_tile['has_impassable_object'] or new_boulder_pos in other_boulders:
            continue
        if find_player_path(player_pos, push_pos, grid, width, height, all_boulders):
            next_states.append((new_boulder_pos, boulder_pos))
    return next_states

def solve_for_boulder(start_boulder, end_switch, player_start_pos, grid, width, height, all_boulders):
    start_state = (start_boulder, player_start_pos)
    open_set = []
    heapq.heappush(open_set, (boulder_heuristic(start_boulder, end_switch), 0, start_state))
    came_from = {start_state: None}
    g_score = {start_state: 0}
    visited_boulder_positions = {start_boulder}
    while open_set:
        _, cost, current_state = heapq.heappop(open_set)
        current_boulder_pos, current_player_pos = current_state
        if current_boulder_pos == end_switch:
            path = []
            temp_state = current_state
            while temp_state is not None:
                path.append(temp_state[0])
                temp_state = came_from.get(temp_state)
            return path[::-1]
        for next_boulder_pos, next_player_pos in get_boulder_push_states(current_state, grid, width, height, all_boulders):
            next_state = (next_boulder_pos, next_player_pos)
            if next_boulder_pos not in visited_boulder_positions:
                visited_boulder_positions.add(next_boulder_pos)
                g_score[next_state] = cost + 1
                f_score = g_score[next_state] + boulder_heuristic(next_boulder_pos, end_switch)
                heapq.heappush(open_set, (f_score, cost + 1, next_state))
                came_from[next_state] = current_state
    return None

try:
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))
    player_start_pos = None
    boulders = []
    switches = []
    grid = []
    all_boulder_coords = set()

    for y, row_elem in enumerate(tree.findall('Row')):
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            coord = (x + 1, y + 1)
            if tile_elem.find('Boulder') is not None:
                all_boulder_coords.add(coord)
                boulders.append({'coordinate': coord})
            if tile_elem.find('Player') is not None:
                player_start_pos = coord

    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            coord = (x + 1, y + 1)
            tile_type = tile_elem.get('type')
            obj_elem = tile_elem.find('Object')
            switch_elem = tile_elem.find('BoulderSwitch')
            has_impassable_object = False
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown']:
                has_impassable_object = True
            tile_data = {'type': tile_type, 'has_impassable_object': has_impassable_object}
            row.append(tile_data)
            if switch_elem is not None:
                switches.append({'coordinate': coord})
        grid.append(row)

    if not player_start_pos:
        raise ValueError("Player position not found on the map.")

    solutions = []
    for boulder in boulders:
        for switch in switches:
            path = solve_for_boulder(boulder['coordinate'], switch['coordinate'], player_start_pos, grid, width, height, all_boulder_coords)
            solutions.append({
                'boulder_start': boulder['coordinate'],
                'switch_end': switch['coordinate'],
                'solution_path': path
            })

    print(json.dumps({'solutions': solutions}, indent=2))

except Exception as e:
    print(json.dumps({'error': str(e)}))
