import json
import sys

def calculate_xp_needed(growth_rate, current_level, current_exp):
    next_level = current_level + 1
    if growth_rate == "Fast":
        next_exp = int(0.8 * (next_level**3))
    elif growth_rate == "Medium Fast":
        next_exp = int(next_level**3)
    elif growth_rate == "Medium Slow":
        next_exp = int(1.2 * (next_level**3) - 15 * (next_level**2) + 100 * next_level - 140)
    elif growth_rate == "Slow":
        next_exp = int(1.25 * (next_level**3))
    else:
        return "Unknown growth rate"

    xp_needed = max(0, next_exp - current_exp)
    return xp_needed

