import collections
import json
import xml.etree.ElementTree as ET

def solve():
    root = ET.fromstring(map_xml_string)
    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'unseen')
            is_wall = t_type in ['WALL', 'WATER', 'BLOCK', 'HEADBUTT_TREE', 'CUT_TREE', 'VOID'] or tile.find('Object') is not None
            if tile.find('Player') is not None: player_pos = (x, y)
            grid[(x, y)] = {'type': t_type, 'wall': is_wall, 'seen': tile.attrib.get('seen') == 'true'}

    if player_pos is None:
        print(json.dumps([]))
        return

    reachable_unseen = []
    queue = collections.deque([player_pos])
    visited = {player_pos}

    while queue:
        cx, cy = queue.popleft()

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid:
                curr = grid[(cx, cy)]
                next_tile = grid[(nx, ny)]

                # Check if next tile is unseen
                if not next_tile['seen']:
                    if (nx, ny) not in reachable_unseen:
                        reachable_unseen.append((nx, ny))
                    continue # Cannot walk into unseen tile

                # Ledge logic
                if curr['type'] == 'LEDGE_HOP_DOWN' and btn != 'Down': continue
                if next_tile['type'] == 'LEDGE_HOP_DOWN' and btn != 'Down': continue

                if next_tile['wall']: continue

                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny))

    print(json.dumps(reachable_unseen))
solve()
