import xml.etree.ElementTree as ET
import json
import heapq

class Node:
    def __init__(self, parent=None, position=None):
        self.parent = parent
        self.position = position
        self.g = 0
        self.h = 0
        self.f = 0
    def __eq__(self, other):
        return self.position == other.position
    def __lt__(self, other):
        return self.f < other.f

class AStar:
    def __init__(self, map_xml_string, object_locations_json):
        root = ET.fromstring(map_xml_string)
        self.width = int(root.attrib['width'])
        self.height = int(root.attrib['height'])
        self.impassable_tiles = {'WALL', 'CUT_TREE', 'HEADBUTT_TREE', 'WATER', 'PC', 'BOOKSHELF', 'COUNTER', 'MART_SHELF', 'RADIO', 'TOWN_MAP', 'TV', 'WINDOW', 'VOID', 'unseen'}
        self.ledge_tiles = {'LEDGE_HOP_DOWN', 'LEDGE_HOP_RIGHT', 'LEDGE_HOP_LEFT'}
        self.grid = [[{'type': 'unseen', 'traversable': 0} for _ in range(self.width)] for _ in range(self.height)]
        self.populate_grid(root, object_locations_json)

    def populate_grid(self, root, object_locations_json):
        object_locations = json.loads(object_locations_json)
        object_coords = set((obj['x'], obj['y']) for obj in object_locations)
        for row_elem in root.findall('Row'):
            y = int(row_elem.attrib['id'])
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.attrib['id'])
                if 0 <= x - 1 < self.width and 0 <= y - 1 < self.height:
                    tile_type = tile_elem.attrib.get('type', 'unseen')
                    self.grid[y - 1][x - 1]['type'] = tile_type
                    if tile_type not in self.impassable_tiles and tile_elem.attrib.get('has-object') != 'true' and (x, y) not in object_coords:
                        self.grid[y - 1][x - 1]['traversable'] = 1

    def find_path(self, start, end):
        self.grid[start[1]][start[0]]['traversable'] = 1
        start_node = Node(None, start)
        end_node = Node(None, end)
        open_list = []
        closed_list = set()
        heapq.heappush(open_list, start_node)
        iteration_count = 0
        while len(open_list) > 0:
            iteration_count += 1
            if iteration_count > 5000: 
                return None
            current_node = heapq.heappop(open_list)
            closed_list.add(current_node.position)
            if current_node == end_node:
                path = []
                current = current_node
                while current is not None:
                    path.append(current.position)
                    current = current.parent
                return path[::-1]
            for new_position in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
                node_position = (current_node.position[0] + new_position[0], current_node.position[1] + new_position[1])
                if not (0 <= node_position[0] < self.width and 0 <= node_position[1] < self.height):
                    continue
                if self.grid[node_position[1]][node_position[0]]['traversable'] == 0:
                    continue
                current_type = self.grid[current_node.position[1]][current_node.position[0]]['type']
                neighbor_type = self.grid[node_position[1]][node_position[0]]['type']
                dx, dy = new_position
                if neighbor_type == 'LEDGE_HOP_RIGHT' and current_type not in self.ledge_tiles and dx != 1: continue
                if neighbor_type == 'LEDGE_HOP_DOWN' and current_type not in self.ledge_tiles and dy != 1: continue
                if neighbor_type == 'LEDGE_HOP_LEFT' and current_type not in self.ledge_tiles and dx != -1: continue
                if current_type == 'LEDGE_HOP_RIGHT' and dx == -1: continue
                if current_type == 'LEDGE_HOP_DOWN' and dy == -1: continue
                if current_type == 'LEDGE_HOP_LEFT' and dx == 1: continue
                if node_position in closed_list:
                    continue
                new_node = Node(current_node, node_position)
                new_node.g = current_node.g + 1
                new_node.h = abs(new_node.position[0] - end_node.position[0]) + abs(new_node.position[1] - end_node.position[1])
                new_node.f = new_node.g + new_node.h
                if any(open_node for open_node in open_list if new_node == open_node and new_node.g > open_node.g):
                    continue
                heapq.heappush(open_list, new_node)
        return None

def find_adjacent_traversable(astar, x, y):
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < astar.width and 0 <= ny < astar.height and astar.grid[ny][nx]['traversable'] == 1:
            return (nx, ny)
    return None

start_x = int(input_data['start_x']) - 1
start_y = int(input_data['start_y']) - 1
end_x = int(input_data['end_x']) - 1
end_y = int(input_data['end_y']) - 1

astar = AStar(map_xml_string, input_data['object_locations_json'])
target_pos = (end_x, end_y)

if astar.grid[end_y][end_x]['traversable'] == 0:
    new_target = find_adjacent_traversable(astar, end_x, end_y)
    if new_target:
        target_pos = new_target
    else:
        print(json.dumps([]))
        exit()

path = astar.find_path((start_x, start_y), target_pos)
if path:
    print(json.dumps([{"x": p[0] + 1, "y": p[1] + 1} for p in path]))
else:
    print(json.dumps([]))

