import json

player_x = int(input_data['player_x'])
player_y = int(input_data['player_y'])
unseen_tiles = json.loads(input_data['unseen_tiles_json'])
failed_tiles = json.loads(input_data['failed_tiles_json'])
direction_filter = input_data.get('direction_filter', 'none')

failed_coords = set((t['x'], t['y']) for t in failed_tiles)

filtered_tiles = []
for tile in unseen_tiles:
    if (tile['x'], tile['y']) in failed_coords:
        continue

    if direction_filter == 'north' and tile['y'] >= player_y:
        continue
    if direction_filter == 'south' and tile['y'] <= player_y:
        continue
    if direction_filter == 'east' and tile['x'] <= player_x:
        continue
    if direction_filter == 'west' and tile['x'] >= player_x:
        continue

    filtered_tiles.append(tile)

if not filtered_tiles:
    print(json.dumps({}))
else:
    closest_tile = None
    min_distance = float('inf')

    for tile in filtered_tiles:
        dist = abs(tile['x'] - player_x) + abs(tile['y'] - player_y)
        if dist < min_distance:
            min_distance = dist
            closest_tile = tile

    print(json.dumps(closest_tile))
