import json

def main(text):
    buttons = []
    cx, cy = 0, 0
    mode = 'upper' # Starts in UPPER

    # Grids
    # Toggle: (0, 5)
    # Space: (6, 2) in both layouts

    # Upper Map
    upper_map = {}
    layout_upper = [
        "ABCDEFGHIJ",
        "KLMNOPQRST",
        "UVWXYZ    ", 
        "1234567890"
    ]

    for y, row in enumerate(layout_upper):
        for x, char in enumerate(row):
            if char != ' ':
                upper_map[char] = (x, y)
    upper_map[' '] = (6, 2)
    upper_map[','] = (7, 2)
    upper_map['?'] = (8, 2)
    upper_map['!'] = (9, 2)

    # Lower Map
    lower_map = {}
    layout_lower = [
        "abcdefghij",
        "klmnopqrst",
        "uvwxyz    ", 
    ]

    for y, row in enumerate(layout_lower):
        for x, char in enumerate(row):
            if char != ' ':
                lower_map[char] = (x, y)
    lower_map[' '] = (6, 2)
    lower_map['.'] = (7, 2)
    lower_map['-'] = (8, 2)
    lower_map['/'] = (9, 2)

    for char in text:
        target = None
        target_mode = mode

        # Identify target coordinates and mode
        if char == ' ':
            target = (6, 2)
        elif char.isalpha():
            if char.isupper():
                target_mode = 'upper'
                target = upper_map.get(char)
            else:
                target_mode = 'lower'
                target = lower_map.get(char)
        elif char.isdigit():
            target_mode = 'upper'
            target = upper_map.get(char)
        elif char in upper_map and mode == 'upper':
             target = upper_map.get(char)
        elif char in lower_map and mode == 'lower':
             target = lower_map.get(char)

        if not target:
            # Fallback for punctuation not in current map
            if char in upper_map:
                target_mode = 'upper'
                target = upper_map.get(char)
            elif char in lower_map:
                target_mode = 'lower'
                target = lower_map.get(char)
            else:
                continue # Skip unknown

        tx, ty = target

        # Switch Mode
        if mode != target_mode:
            # Go to toggle (0, 5)
            if cx > 0: buttons.extend(["Left"] * cx)
            cx = 0

            if cy < 5: buttons.extend(["Down"] * (5 - cy))
            elif cy > 5: buttons.extend(["Up"] * (cy - 5))
            cy = 5

            buttons.append("A")
            mode = target_mode

        # Move to Target
        if ty > cy: buttons.extend(["Down"] * (ty - cy))
        elif ty < cy: buttons.extend(["Up"] * (cy - ty))
        cy = ty

        if tx > cx: buttons.extend(["Right"] * (tx - cx))
        elif tx < cx: buttons.extend(["Left"] * (cx - tx))
        cx = tx

        buttons.append("A")

    return buttons

