inventory_navigator_tool.python_script:
import json
try:
    inventory = json.loads(input_data['inventory_list'])
    current_item = input_data['current_selection']
    target_item = input_data['target_item']

    if current_item not in inventory or target_item not in inventory:
        print(json.dumps({'error': 'Item not found in inventory list.'}))
    else:
        current_idx = inventory.index(current_item)
        target_idx = inventory.index(target_item)

        response = {}
        if current_idx == target_idx:
            response = {'direction': 'NONE', 'presses': 0}
        elif target_idx > current_idx:
            presses = target_idx - current_idx
            response = {'direction': 'DOWN', 'presses': presses}
        else: # target_idx < current_idx
            presses = current_idx - target_idx
            response = {'direction': 'UP', 'presses': presses}

        if response.get('presses', 0) > 4:
            response['note'] = 'Warning: This is a long scroll. Press Down in increments of 4 to navigate pages more effectively.'

        print(json.dumps(response))
except Exception as e:
    print(json.dumps({'error': str(e), 'type': type(e).__name__}))

check_tile_walkable.python_script:
import xml.etree.ElementTree as ET
import json

def is_walkable(x, y, grid):
    tile = grid.get((x, y))
    print(f"Inside is_walkable: Checking tile ({x}, {y})")
    if not tile:
        print("Result: False (Tile not in grid)")
        return False
    tile_type = tile.get('type')
    if tile_type in ['impassable']:
        return False
    return True

map_xml_string = globals().get('map_xml_string', '')
if not map_xml_string:
    print(json.dumps({'error': 'map_xml_string not found'}))
else:
    try:
        root = ET.fromstring(map_xml_string)
        grid = {}
        for row in root.findall('Row'):
            y_coord = int(row.get('id'))
            for tile_node in row.findall('Tile'):
                x_coord = int(tile_node.get('id'))
                grid[(x_coord, y_coord)] = tile_node

        x_to_check = int(input_data['x'])
        y_to_check = int(input_data['y'])

        print(f"Grid populated with {len(grid)} tiles.")
        walkable_status = is_walkable(x_to_check, y_to_check, grid)
        print(f"Final determination for ({x_to_check}, {y_to_check}): {'Walkable' if walkable_status else 'Not Walkable'}")

    except Exception as e:
        print(json.dumps({'error': f'An unexpected error occurred: {e}'}))
