import json
import collections
import xml.etree.ElementTree as ET

def find_path():
    try:
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])
        movement_state = input_data['movement_state']

        root = ET.fromstring(map_xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))
        tiles = {}
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile_elem in row.findall('Tile'):
                x = int(tile_elem.get('id'))
                obj = tile_elem.find('Object')
                tiles[(x, y)] = {
                    'type': tile_elem.get('type'),
                    'has_object': tile_elem.get('has-object') == 'true',
                    'object_id_name': obj.get('id-name') if obj is not None else None
                }

        start = (start_x, start_y)
        end = (end_x, end_y)

        base_traversable_tile_types = {
            'ground', 'grass', 'elevated_ground', 'steps', 'ladder_up', 
            'ladder_down', 'teleport', 'hole', 'cleared_boulder_barrier',
            'open_gate', 'gate_offscreen'
        }

        if movement_state == 'surfing':
            base_traversable_tile_types.add('water')

        def is_valid_move(pos, from_pos=None):
            x, y = pos
            if not (1 <= x <= map_width and 1 <= y <= map_height):
                return False

            tile_info = tiles.get(pos)
            if not tile_info:
                return False

            tile_type = tile_info['type']
            if tile_type == 'impassable':
                return False

            if tile_type not in base_traversable_tile_types:
                return False

            if tile_info['has_object'] and (tile_info.get('object_id_name') or '').upper() != 'PIKACHU':
                return False

            if from_pos:
                current_tile_info = tiles.get(from_pos)
                current_type = current_tile_info.get('type')
                next_type = tile_info.get('type')

                is_elevation_change = (current_type == 'ground' and next_type == 'elevated_ground') or \
                                      (current_type == 'elevated_ground' and next_type == 'ground')

                is_via_steps = (current_type == 'steps' or next_type == 'steps')

                if is_elevation_change and not is_via_steps:
                    return False

            return True

        goal_tiles = []
        if is_valid_move(end):
            goal_tiles.append(end)
        else:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_pos = (end_x + dx, end_y + dy)
                if is_valid_move(adj_pos):
                    goal_tiles.append(adj_pos)

        if not goal_tiles:
            reason = f'Destination ({end_x},{end_y}) and all adjacent tiles are impassable.'
            print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))
            return

        queue = collections.deque([(start, [start])])
        visited = {start}

        while queue:
            current_pos, path = queue.popleft()

            if current_pos in goal_tiles:
                final_path_plan = [{'x': p[0], 'y': p[1]} for p in path]
                print(json.dumps({'success': True, 'reason': 'Path found.', 'path_plan': final_path_plan}))
                return

            (cx, cy) = current_pos
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                next_pos = (cx + dx, cy + dy)

                if next_pos in visited:
                    continue

                if tiles.get(next_pos, {}).get('type') == 'ledge':
                    if dy == 1 and dx == 0:
                        final_pos = (cx, cy + 2)
                        if final_pos not in visited and is_valid_move(final_pos, from_pos=current_pos):
                            visited.add(final_pos)
                            new_path = list(path)
                            new_path.append(final_pos)
                            queue.append((final_pos, new_path))
                    continue

                if is_valid_move(next_pos, from_pos=current_pos):
                    visited.add(next_pos)
                    new_path = list(path)
                    new_path.append(next_pos)
                    queue.append((next_pos, new_path))

        # If queue is empty, check if destination is in a separate partition
        is_end_tile_in_visited = end in visited
        is_any_goal_tile_in_visited = any(gt in visited for gt in goal_tiles)

        if not is_end_tile_in_visited and not is_any_goal_tile_in_visited:
            print(json.dumps({'success': False, 'reason': f'Destination ({end_x},{end_y}) is in an unreachable partition of the map.', 'path_plan': []}))
            return

        print(json.dumps({'success': False, 'reason': 'No path found.', 'path_plan': []}))

    except Exception as e:
        print(json.dumps({'success': False, 'reason': f'An error occurred: {str(e)}', 'path_plan': []}))

find_path()

menu_analyzer.python_script:
import json
import re

try:
    screen_text = input_data['screen_text']
    lines = [line.strip() for line in screen_text.strip().split('\n') if line.strip()]

    options = []
    cursor_position = None
    layout = 'single_column'
    cursor_char = ''

    # Define specific menu types
    is_pc_main_menu = "WITHDRAW PKMN" in screen_text and "SEE YA!" in screen_text
    is_pc_pokemon_list = "BOX No." in screen_text and not is_pc_main_menu
    is_pc_withdraw_submenu = all(keyword in screen_text for keyword in ['WITHDRAW', 'STATS', 'CANCEL'])
    is_move_selection = "TYPE/" in screen_text
    is_party_submenu = all(keyword in screen_text for keyword in ['STATS', 'SWITCH', 'CANCEL'])
    is_party_menu = "Choose a POKMON" in screen_text or "Bring out which" in screen_text

    if is_pc_main_menu:
        layout = 'single_column'
        for line in lines:
            if "BOX No." not in line:
                clean_line = line.replace(cursor_char, '').strip()
                options.append(clean_line)
                if cursor_char in line:
                    cursor_position = clean_line
        options = [opt for opt in options if opt]
    elif is_pc_withdraw_submenu:
        layout = 'single_column'
        options = ['WITHDRAW', 'STATS', 'CANCEL']
        for line in lines:
            if cursor_char in line:
                for opt in options:
                    if opt in line:
                        cursor_position = opt
                        break
    elif is_pc_pokemon_list:
        layout = 'single_column'
        pokemon_names = []
        cursor_name = None
        for line in lines:
            if "BOX No." not in line and not line.startswith("LV"):
                clean_line = re.sub(r'^[A-Z]{2}\s+', '', line)
                words = clean_line.replace(cursor_char, '').strip().split()
                if words:
                    name = next((word for word in words if word.isupper() and len(word) > 1), None)
                    if name:
                        pokemon_names.append(name)
                        if cursor_char in line:
                            cursor_name = name
        options = pokemon_names
        cursor_position = cursor_name
    elif is_move_selection:
        layout = 'single_column'
        move_options = []
        cursor_move = None
        start_parsing = False
        for line in lines:
            if re.search(r'\d+/\d+', line):
                start_parsing = True
                continue
            if start_parsing:
                clean_line = line.replace(cursor_char, '').strip()
                if clean_line and re.match(r'^[A-Z\s-]+$', clean_line):
                    move_options.append(clean_line)
                    if cursor_char in line:
                        cursor_move = clean_line
        options = move_options
        cursor_position = cursor_move
    elif is_party_submenu:
        layout = 'single_column'
        options = ['STATS', 'SWITCH', 'CANCEL']
        for line in lines:
            if cursor_char in line:
                for opt in options:
                    if opt in line:
                        cursor_position = opt
                        break
    elif is_party_menu:
        layout = 'single_column'
        pokemon_names = []
        cursor_name = None
        for i, line in enumerate(lines):
            match = re.search(r"([A-Z']{2,})\s+LV", line)
            if match:
                name = match.group(1)
                pokemon_names.append(name)
                if i + 1 < len(lines) and lines[i+1].strip().startswith(cursor_char):
                    cursor_name = name
        options = pokemon_names
        cursor_position = cursor_name
    else:
        is_multi_column = any(re.search(r'\S\s{2,}\S', line) for line in lines)
        if is_multi_column:
            layout = '2x2_grid'
            flat_options = []
            cursor_opt = None
            for line in lines:
                parts = re.split(r'\s{2,}', line)
                for part in parts:
                    if part:
                        clean_part = part.replace(cursor_char, '').strip()
                        flat_options.append(clean_part)
                        if cursor_char in part:
                            cursor_opt = clean_part
            options = flat_options
            cursor_position = cursor_opt
        else:
            layout = 'single_column'
            for line in lines:
                clean_line = line.replace(cursor_char, '').strip()
                if clean_line:
                    options.append(clean_line)
                    if cursor_char in line:
                        cursor_position = clean_line

    result = {
        'options': options,
        'cursor_position': cursor_position,
        'layout': layout
    }
    print(json.dumps(result))

except Exception as e:
    print(json.dumps({'error': str(e), 'options': [], 'cursor_position': None, 'layout': 'unknown'}))

select_move_in_battle.python_script:
import json
import re

def select_move():
    screen_text = input_data['screen_text']
    target_move = input_data['target_move'].upper()
    stage = input_data['stage']

    lines = screen_text.strip().split('\n')

    pp_line_index = -1
    for i, line in enumerate(lines):
        if re.search(r'\d+/\d+', line):
            pp_line_index = i
            break

    if pp_line_index == -1:
        print(json.dumps([]))
        return

    raw_move_lines = lines[pp_line_index + 1 : pp_line_index + 5]
    if len(raw_move_lines) < 4:
        print(json.dumps([]))
        return

    moves = []
    cursor_pos = -1
    cursor_char = ''
    for i, line in enumerate(raw_move_lines):
        clean_line = line.strip()
        if cursor_char in clean_line:
            cursor_pos = i
            clean_line = clean_line.replace(cursor_char, '').strip()
        moves.append(clean_line)

    if cursor_pos == -1:
        print(json.dumps([]))
        return

    try:
        target_pos = moves.index(target_move)
    except ValueError:
        print(json.dumps([]))
        return

    buttons = []
    if stage == 'navigate':
        diff = target_pos - cursor_pos
        if diff > 0:
            buttons.extend(['Down'] * diff)
        elif diff < 0:
            buttons.extend(['Up'] * abs(diff))
    elif stage == 'confirm':
        if cursor_pos == target_pos:
             buttons.extend(['A', 'A', 'A'])

    print(json.dumps(buttons))

select_move()

rock_checker.python_script:
import json
import xml.etree.ElementTree as ET
import math

def find_next_rock():
    try:
        player_x = int(input_data['player_x'])
        player_y = int(input_data['player_y'])

        root = ET.fromstring(map_xml_string)

        potential_rocks = []
        all_tiles = {}
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                all_tiles[(x, y)] = tile_elem

        for (x, y), tile_elem in all_tiles.items():
            if tile_elem.get('type') == 'impassable':
                is_checked = False
                for marker in tile_elem.findall('Marker'):
                    if marker.get('emoji') == '':
                        is_checked = True
                        break
                if is_checked:
                    continue

                is_potential_rock = False
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    adj_tile = all_tiles.get((x + dx, y + dy))
                    if adj_tile is not None and adj_tile.get('type') == 'ground':
                        is_potential_rock = True
                        break

                if is_potential_rock:
                    potential_rocks.append((x, y))

        if not potential_rocks:
            print(json.dumps({'success': False, 'reason': 'No unchecked potential rocks found.'}))
            return

        closest_rock = None
        min_dist = float('inf')
        for rock_x, rock_y in potential_rocks:
            dist = math.sqrt((player_x - rock_x)**2 + (player_y - rock_y)**2)
            if dist < min_dist:
                min_dist = dist
                closest_rock = (rock_x, rock_y)

        stand_pos = None
        blocking_object_info = None
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = closest_rock[0] + dx, closest_rock[1] + dy
            adj_tile = all_tiles.get((adj_x, adj_y))
            if adj_tile is not None and adj_tile.get('type') == 'ground':
                has_blocking_object = False
                obj = adj_tile.find('Object')
                if obj is not None and (obj.get('id-name') or '').upper() != 'PIKACHU':
                    has_blocking_object = True
                    blocking_object_info = {
                        "name": obj.get('id-name') or obj.get('name', 'Unknown Object'),
                        "x": adj_x,
                        "y": adj_y
                    }

                if not has_blocking_object:
                    stand_pos = {'x': adj_x, 'y': adj_y}
                    break

        if stand_pos:
            print(json.dumps({
                'success': True, 
                'rock_coords': {'x': closest_rock[0], 'y': closest_rock[1]}, 
                'stand_coords': stand_pos
            }))
        else:
            if blocking_object_info:
                reason = f"Found closest rock at {closest_rock}, but all valid adjacent tiles are blocked. Obstacle: {blocking_object_info['name']} at ({blocking_object_info['x']}, {blocking_object_info['y']})."
            else:
                reason = f'Found closest rock at {closest_rock}, but no valid adjacent tile to stand on.'
            print(json.dumps({'success': False, 'reason': reason}))

    except Exception as e:
        print(json.dumps({'success': False, 'reason': f'An error occurred: {str(e)}'}))

find_next_rock()
