import xml.etree.ElementTree as ET
import heapq
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(node, grid, width, height, movement_mode):
    x, y = node
    neighbors = []
    current_tile = grid[y-1][x-1]
    current_type = current_tile['type']

    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
    surfable_types = {'water'}
    impassable_types = {'impassable', 'unknown'}

    for dx, dy, move in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
        nx, ny = x + dx, y + dy

        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny-1][nx-1]
        neighbor_type = neighbor_tile['type']

        if neighbor_type in impassable_types:
            continue

        if neighbor_tile['has_impassable_object'] or neighbor_tile['has_boulder']:
            continue

        if movement_mode == 'walking':
            if neighbor_type == 'ledge':
                if move == 'Down':
                    nnx, nny = nx, ny + 1
                    if 1 <= nnx <= width and 1 <= nny <= height:
                        landing_tile = grid[nny-1][nnx-1]
                        if landing_tile['type'] in walkable_types and not landing_tile['has_impassable_object'] and not landing_tile['has_boulder']:
                            neighbors.append(((nnx, nny), move))
                continue

            is_walkable = neighbor_type in walkable_types
            if not is_walkable:
                continue

            move_is_valid = False
            current_is_elevated = current_type in ['elevated_ground', 'cleared_boulder_barrier']
            neighbor_is_elevated = neighbor_type in ['elevated_ground', 'cleared_boulder_barrier']

            if current_is_elevated == neighbor_is_elevated:
                move_is_valid = True
            elif (current_type == 'steps' or neighbor_type == 'steps'):
                move_is_valid = True
            elif current_is_elevated and not neighbor_is_elevated and neighbor_type == 'ground':
                move_is_valid = True

            if move_is_valid:
                neighbors.append(((nx, ny), move))

        elif movement_mode == 'surfing':
            if neighbor_type in surfable_types:
                neighbors.append(((nx, ny), move))

    return neighbors

def a_star_search(start, end, grid, width, height, movement_mode):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(came_from[current][1])
                current = came_from[current][0]
            return path[::-1]

        for neighbor, move in get_neighbors(current, grid, width, height, movement_mode):
            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = (current, move)
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    movement_mode = input_data.get('movement_mode', 'walking')
    extra_impassable_str = input_data.get('extra_impassable', '[]')
    extra_impassable = json.loads(extra_impassable_str)

    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))
    map_id = int(tree.get('id'))

    grid = []
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_type = tile_elem.get('type')
            obj_elem = tile_elem.find('Object')
            has_impassable_object = False
            if obj_elem is not None:
                obj_id_name = obj_elem.get('id-name', '')
                is_pikachu = obj_id_name in ['Pikachu', 'Unknown']
                is_passable_guard = (map_id == 34 and obj_id_name == 'Guard')
                if not is_pikachu and not is_passable_guard:
                    has_impassable_object = True

            has_boulder = tile_elem.find('Boulder') is not None
            if [x+1, y+1] in extra_impassable:
                has_impassable_object = True

            row.append({'type': tile_type, 'has_impassable_object': has_impassable_object, 'has_boulder': has_boulder})
        grid.append(row)

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    path = a_star_search(start_node, end_node, grid, width, height, movement_mode)

    if path:
        print(json.dumps(path))
    else:
        valid_adjacent_types = set()
        if movement_mode == 'walking':
            valid_adjacent_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
        elif movement_mode == 'surfing':
            valid_adjacent_types = {'water'}

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = end_x + dx, end_y + dy
            if 1 <= adj_x <= width and 1 <= adj_y <= height:
                adj_node = (adj_x, adj_y)
                adj_tile = grid[adj_y-1][adj_x-1]
                if adj_tile['type'] in valid_adjacent_types and not adj_tile['has_impassable_object'] and not adj_tile['has_boulder']:
                    path_to_adj = a_star_search(start_node, adj_node, grid, width, height, movement_mode)
                    if path_to_adj:
                        print(json.dumps(path_to_adj))
                        exit()
        print(json.dumps({"error": "No path found"}))

except Exception as e:
    print(json.dumps({"error": str(e)}))

