import xml.etree.ElementTree as ET
import json

root = ET.fromstring(map_xml_string)
player_pos = None
boulders = []
grid_layout = {}

for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        coord_str = f'({x},{y})'
        grid_layout[coord_str] = tile_elem.get('type')
        if tile_elem.find('Player') is not None:
            player_pos = {'x': x, 'y': y}
        if tile_elem.find('Boulder') is not None:
            boulders.append({'x': x, 'y': y})

target_switch = {'x': int(input_data['target_x']), 'y': int(input_data['target_y'])}

agent_call = {
    "tool_name": "puzzle_strategist_agent",
    "tool_args": [
        {"name": "grid_layout", "value": json.dumps(grid_layout)},
        {"name": "player_pos", "value": json.dumps(player_pos)},
        {"name": "boulders", "value": json.dumps(boulders)},
        {"name": "target_switch", "value": json.dumps(target_switch)}
    ]
}

print(json.dumps(agent_call, indent=2))
