    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_obj = tile.get('has-object') == 'true'
            if tile.find('Player') is not None:
                player_pos = (x, y)
            grid[(x, y)] = {'type': t_type, 'seen': seen, 'has_obj': has_obj}

    if player_pos is None:
        print(json.dumps([]))
        return

    def is_traversable(x, y, m):
        if (x, y) not in grid: return True
        tile = grid[(x, y)]
        if not tile['seen']: return True
        if tile['type'] in ['WALL', 'BUOY', 'FLOOR_UP_WALL', 'BREAKABLE ROCK']: return False
        if tile['has_obj']: return False
        if m == 'WATER':
            return tile['type'] == 'WATER'
        else:
            return tile['type'] in ['FLOOR', 'DOOR', 'LADDER', 'WARP_CARPET_RIGHT', 'WARP_CARPET_LEFT', 'WARP_CARPET_UP', 'WARP_CARPET_DOWN', 'LEDGE_HOP_DOWN']

    queue = deque([(player_pos[0], player_pos[1], [])])
    visited = {player_pos}
    while queue:
        x, y, path = queue.popleft()
        if (x, y) == (target_x, target_y):
            print(json.dumps(path))
            return
        for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if is_traversable(nx, ny, medium):
                    if medium == 'LAND' and dy == -1:
                        t = grid.get((nx, ny), {})
                        if t.get('seen') and t.get('type') == 'LEDGE_HOP_DOWN':
                            continue
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
    print(json.dumps([]))

solve()

