import xml.etree.ElementTree as ET
import json
import heapq

def find_closest_unseen_tile_script(map_xml_string, input_data):
    try:
        party_data = json.loads(input_data['party_data'])
        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        start_x, start_y = -1, -1
        player_found = False
        unseen_tiles = []
        tile_map = {}

        for row_elem in root.findall('Row'):
            y = int(row_elem.attrib['id'])
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.attrib['id'])
                tile_map[(x - 1, y - 1)] = tile_elem
                if tile_elem.attrib.get('seen') != 'true':
                    unseen_tiles.append((x, y))
                if tile_elem.attrib.get('has-player') == 'true':
                    start_x, start_y = x, y
                    player_found = True

        if not player_found:
            return json.dumps({"error": "Player not found on map."})

        start = (start_x - 1, start_y - 1)

        walkable_types = {'ground', 'grass', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'steps', 'elevated_ground', 'teleport', 'hole', 'ladder_up', 'ladder_down', 'spinner_stop', 'boulder_switch'}

        has_surf = any('SURF' in move['name'] for pokemon in party_data for move in pokemon['moves'])
        if has_surf:
            walkable_types.add('water')

        def get_tile_type(x, y):
            tile_elem = tile_map.get((x, y))
            return tile_elem.attrib.get('type', 'unknown') if tile_elem is not None else 'unknown'

        def is_impassable(x, y):
            tile_elem = tile_map.get((x, y))
            if tile_elem is None: return True
            tile_type = tile_elem.attrib.get('type', 'unknown')
            if tile_type == 'ledge': return True
            return (
                tile_type not in walkable_types or
                (tile_elem.find('Object') is not None and tile_elem.find(".//Object[@id-name='Pikachu']") is None)
            )

        queue = [start]
        reachable_tiles = {start}

        while queue:
            current = queue.pop(0)

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = (current[0] + dx, current[1] + dy)

                if not (0 <= neighbor[0] < width and 0 <= neighbor[1] < height):
                    continue

                if neighbor in reachable_tiles:
                    continue

                neighbor_type = get_tile_type(neighbor[0], neighbor[1])
                actual_neighbor = neighbor

                if neighbor_type == 'ledge' and dy == 1:
                    actual_neighbor = (neighbor[0], neighbor[1] + 1)
                    if not (0 <= actual_neighbor[0] < width and 0 <= actual_neighbor[1] < height) or is_impassable(actual_neighbor[0], actual_neighbor[1]):
                        continue
                elif is_impassable(neighbor[0], neighbor[1]):
                    continue

                if actual_neighbor not in reachable_tiles:
                    reachable_tiles.add(actual_neighbor)
                    queue.append(actual_neighbor)

        border_unseen_tiles = []
        for ux, uy in unseen_tiles:
            unseen_coord_0based = (ux - 1, uy - 1)
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_tile = (unseen_coord_0based[0] + dx, unseen_coord_0based[1] + dy)
                if adj_tile in reachable_tiles:
                    border_unseen_tiles.append((ux, uy))
                    break

        if not border_unseen_tiles:
            return json.dumps({'message': 'No reachable unseen tiles found.'})

        closest_tile = min(border_unseen_tiles, key=lambda t: abs(t[0] - start_x) + abs(t[1] - start_y))

        closest_tile_0based = (closest_tile[0] - 1, closest_tile[1] - 1)

        best_adj_tile = None
        min_dist = float('inf')

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_tile = (closest_tile_0based[0] + dx, closest_tile_0based[1] + dy)
            if adj_tile in reachable_tiles:
                dist = abs(adj_tile[0] - start[0]) + abs(adj_tile[1] - start[1])
                if dist < min_dist:
                    min_dist = dist
                    best_adj_tile = adj_tile

        if best_adj_tile:
            return json.dumps({
                'closest_reachable_unseen_tile': {'x': closest_tile[0], 'y': closest_tile[1]},
                'adjacent_tile': {'x': best_adj_tile[0] + 1, 'y': best_adj_tile[1] + 1}
            })
        else:
            return json.dumps({'message': 'Found a reachable unseen tile, but no reachable adjacent tile to path to.'})

    except Exception as e:
        import traceback
        return json.dumps({'error': str(e), 'traceback': traceback.format_exc()})

print(find_closest_unseen_tile_script(map_xml_string, input_data))
