import xml.etree.ElementTree as ET
import heapq
import json

def find_and_convert_path(start_x, start_y, target_x, target_y, map_xml_string):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    objects = set()

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            grid[(x, y)] = tile.get('type')
            if tile.find('Object') is not None and tile.find('Object').get('id-name') != 'Pikachu':
                objects.add((x, y))

    def is_walkable(x, y):
        if (x, y) not in grid:
            return False
        if (x, y) in objects:
            return False
        tile_type = grid.get((x,y))
        walkable_types = [
            'ground', 'grass', 'steps', 'elevated_ground', 
            'cleared_boulder_barrier', 'open_gate', 'ladder_up', 
            'ladder_down', 'hole', 'spinner_stop', 'teleport',
        ]
        is_walkable_type = tile_type in walkable_types or 'spinner' in tile_type
        return is_walkable_type

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) not in grid:
                continue

            if grid.get((nx, ny)) == 'ledge' and dy == 1 and grid.get((x,y)) != 'ledge':
                if is_walkable(nx, ny + 1):
                    neighbors.append((nx, ny + 1, 'Down'))
                continue
            if is_walkable(nx, ny):
                neighbors.append((nx, ny, move))
        return neighbors

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    start_node = (start_x, start_y)

    target_nodes = set()
    if is_walkable(target_x, target_y):
        target_nodes.add((target_x, target_y))
    else:
        for dx, dy, _ in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if is_walkable(adj_x, adj_y):
                target_nodes.add((adj_x, adj_y))

    if not target_nodes:
        print(json.dumps([]))
        return

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: min(heuristic(start_node, t) for t in target_nodes)}

    path_found = False
    while open_set:
        _, current = heapq.heappop(open_set)

        if current in target_nodes:
            path_moves = []
            temp = current
            while temp in came_from:
                prev, move = came_from[temp]
                path_moves.append(move)
                temp = prev

            moves = path_moves[::-1]
            x, y = start_x, start_y
            coordinates = [{'x': x, 'y': y}]
            move_map = {'Up': (0, -1), 'Down': (0, 1), 'Left': (-1, 0), 'Right': (1, 0)}

            for move in moves:
                if move in move_map:
                    dx, dy = move_map[move]
                    x += dx
                    y += dy
                    coordinates.append({'x': x, 'y': y})
            print(json.dumps(coordinates))
            path_found = True
            break

        for neighbor_x, neighbor_y, move in get_neighbors(current[0], current[1]):
            neighbor = (neighbor_x, neighbor_y)
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = (current, move)
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + min(heuristic(neighbor, t) for t in target_nodes)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

    if not path_found:
        print(json.dumps([]))

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    find_and_convert_path(start_x, start_y, target_x, target_y, map_xml_string)
except Exception as e:
    print(f'Error executing path_planner: {e}')
