import xml.etree.ElementTree as ET
import json
from collections import deque
import heapq

def parse_map(map_xml_string):
    root = ET.fromstring(map_xml_string)
    grid = {}
    unseen_tiles = set()
    all_impassable = set()
    impassable_marker_emojis = {'ðŸ“', 'ðŸš€', 'ðŸ‘¦', 'ðŸ‘©â€ðŸ«', 'ðŸ‘´', 'âš”ï¸', 'ðŸ’°', 'ðŸŽ£', 'ðŸ˜Ž', 'ðŸŽ', 'â“', 'ðŸ¤“'}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            grid[(x, y)] = {'type': tile_type}
            if tile_elem.get('seen') == 'false':
                unseen_tiles.add((x, y))
            if tile_elem.find('Object') is not None:
                all_impassable.add((x, y))
            for marker_elem in tile_elem.findall('Marker'):
                if marker_elem.get('emoji') in impassable_marker_emojis:
                    all_impassable.add((x, y))
    return grid, unseen_tiles, all_impassable

def is_traversable(grid, pos, all_impassable, avoid_coords_set):
    if pos in avoid_coords_set:
        return False
    if pos not in grid:
        return False
    impassable_types = {
        'WALL', 'HEADBUTT_TREE', 'PILLAR', 'BOOKSHELF', 'TV', 'RADIO', 'TOWN_MAP', 'WINDOW', 'STATUE', 'TABLE', 'CHAIR', 'BIRD', 
        'MART_SHELF', 'BUOY', 'PC', 'LINK_CABLE', 'TRADE_MACHINE', 'INCENSE_BURNER', 'COUNTER', 'ROOF', 'CHIMNEY', 'SIGN', 
        'FLOWER', 'TREE_TOP', 'WATER_EDGE_UP', 'WATER_EDGE_DOWN', 'WATER_EDGE_LEFT', 'WATER_EDGE_RIGHT', 'VOID', 'PIT'
    }
    tile_info = grid.get(pos)
    if not tile_info or tile_info['type'] in impassable_types or pos in all_impassable:
        return False
    return True

def get_neighbors(grid, pos):
    neighbors = []
    for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
        next_pos = (pos[0] + dx, pos[1] + dy)
        if next_pos in grid:
            current_type = grid[pos]['type']
            next_type = grid[next_pos]['type']
            if current_type == 'LEDGE_HOP_DOWN' and dy != 1: continue
            if current_type == 'LEDGE_HOP_LEFT' and dx != -1: continue
            if current_type == 'LEDGE_HOP_RIGHT' and dx != 1: continue
            if next_type == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if next_type == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if next_type == 'LEDGE_HOP_RIGHT' and dx == -1: continue
            if next_type == 'FLOOR_UP_WALL' and dy != -1: continue
            neighbors.append(next_pos)
    return neighbors

def find_path_astar(grid, start, end, all_impassable, avoid_coords_set):
    if not is_traversable(grid, start, all_impassable, avoid_coords_set):
        return None, f'Start {start} is impassable.'
    if start not in grid or end not in grid:
        return None, 'Start or end coordinates are outside the map boundaries.'
    if not is_traversable(grid, end, all_impassable, avoid_coords_set):
        found_adjacent = False
        for neighbor in get_neighbors(grid, end):
            if is_traversable(grid, neighbor, all_impassable, avoid_coords_set):
                end = neighbor
                found_adjacent = True
                break
        if not found_adjacent:
            return None, f'End {end} and all its neighbors are impassable.'
    open_set = [(0, start)]; came_from = {}; g_score = {start: 0}
    iteration = 0
    while open_set:
        iteration += 1
        if iteration > 10000: return None, 'Max iterations exceeded.'
        _, current = heapq.heappop(open_set)
        if current == end:
            path = []
            while current in came_from: path.append(current); current = came_from[current]
            path.append(start)
            return path[::-1], None
        for neighbor in get_neighbors(grid, current):
            if not is_traversable(grid, neighbor, all_impassable, avoid_coords_set):
                continue
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current; g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1])
                heapq.heappush(open_set, (f_score, neighbor))
    return None, f'No path found from {start} to {end}.'

def find_all_unseen_areas(grid, unseen_tiles):
    visited = set()
    all_areas = []
    for tile in unseen_tiles:
        if tile not in visited:
            current_area = []
            q = deque([tile])
            visited.add(tile)
            while q:
                node = q.popleft()
                current_area.append(node)
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbor = (node[0] + dx, node[1] + dy)
                    if neighbor in unseen_tiles and neighbor not in visited:
                        visited.add(neighbor); q.append(neighbor)
            all_areas.append(current_area)
    return all_areas

try:
    start_x = int(input_data['start_x']); start_y = int(input_data['start_y'])
    explore_unseen = input_data.get('explore_unseen', 'false').lower() == 'true'
    avoid_coords_raw = json.loads(input_data.get('avoid_coords', '[]'))
    avoid_coords_set = set(tuple(c) for c in avoid_coords_raw) if avoid_coords_raw else set()
    grid, unseen_tiles, all_impassable = parse_map(map_xml_string)
    start_pos = (start_x, start_y)
    path, error = None, None
    if explore_unseen:
        if not unseen_tiles:
            error = 'No unseen areas to explore.'
        else:
            all_unseen_blobs = find_all_unseen_areas(grid, unseen_tiles)
            all_entry_points = set()
            for blob in all_unseen_blobs:
                for unseen_tile in blob:
                    for neighbor in get_neighbors(grid, unseen_tile):
                        if is_traversable(grid, neighbor, all_impassable, avoid_coords_set):
                            all_entry_points.add(neighbor)
            if not all_entry_points:
                error = 'No traversable entry points found for any unseen area.'
            else:
                shortest_path = None
                for entry in all_entry_points:
                    p, e = find_path_astar(grid, start_pos, entry, all_impassable, avoid_coords_set)
                    if p and (shortest_path is None or len(p) < len(shortest_path)):
                        shortest_path = p
                if shortest_path:
                    path = shortest_path
                else:
                    error = 'No reachable entry points to any unseen area found.'
    else:
        end_x = int(input_data.get('end_x')) if input_data.get('end_x') is not None else None
        end_y = int(input_data.get('end_y')) if input_data.get('end_y') is not None else None
        if end_x is None or end_y is None:
            error = 'No target specified. Provide end coordinates or set explore_unseen to true.'
        else:
            path, error = find_path_astar(grid, start_pos, (end_x, end_y), all_impassable, avoid_coords_set)
    path_coords = [[p[0], p[1]] for p in path] if path else []
    print(json.dumps({'path': path_coords, 'error': error}))
except Exception as e:
    import traceback
    print(json.dumps({'path': [], 'error': f'An exception occurred: {str(e)}\n{traceback.format_exc()}'}))
