import collections
import xml.etree.ElementTree as ET
import json

def bfs(map_data, start, target):
    width = int(map_data.attrib['width'])
    height = int(map_data.attrib['height'])

    def get_tile(x, y):
        if 0 <= x < width and 0 <= y < height:
            row = map_data.find(f".//Row[@id='{y}']")
            if row is not None:
                return row.find(f"Tile[@id='{x}']")
        return None

    def is_passable(tile, from_x, from_y, to_x, to_y):
        if tile is None: return False
        t_type = tile.attrib.get('type')
        if t_type in ['WALL', 'BUOY']: return False
        if tile.find('Object') is not None: return False

        if t_type == 'FLOOR_UP_WALL':
            if from_y > to_y: return True # South to North
            return False
        if t_type == 'LEDGE_HOP_DOWN':
            if from_y < to_y: return True # North to South
            return False

        return True

    queue = collections.deque([(start, [])])
    visited = {start}

    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == target:
            return path

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) not in visited:
                target_tile = get_tile(nx, ny)
                if is_passable(target_tile, x, y, nx, ny):
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [btn]))
    return None

